import React, { useState, useEffect, useRef, useCallback } from "react";
import * as faceapi from "face-api.js";
import Webcam from "react-webcam";
import forbiddenWordsList from "./forbiddenWords.json";
import "./ContinuousAuth.css";
import axios from "axios";

// ðŸ”Š Function to play alert sound
const playAlertSound = () => {
  const audio = new Audio("/alert-sound.mp3");
  audio.play().catch((err) => console.error("Error playing sound", err));
};

// âš ï¸ Custom alert with sound
const showAlertWithSound = (message) => {
  playAlertSound();
  alert(message);
};

const ContinuousAuth = () => {
  const webcamRef = useRef(null);
  const recognitionRef = useRef(null);
  const [faceDetected, setFaceDetected] = useState(false);
  const [soundDetected, setSoundDetected] = useState(false);
  const [currentImage, setCurrentImage] = useState(null);
  const [permissionsGranted, setPermissionsGranted] = useState(false);
  const [examTerminated, setExamTerminated] = useState(false);
  const [mediaStream, setMediaStream] = useState(null);
  const [showPermissionMessage, setShowPermissionMessage] = useState(true);
  const [noFaceDuration, setNoFaceDuration] = useState(0);
  const [transcript, setTranscript] = useState("");
  const [forbiddenCount, setForbiddenCount] = useState(0);
  const detectedForbiddenWords = useRef(new Set());
  const forbiddenWords = forbiddenWordsList.words;
  const [referenceDescriptor, setReferenceDescriptor] = useState(null);
  const [violationCount, setViolationCount] = useState(0);
  const [capturedImages, setCapturedImages] = useState([]);
  const [faceAbsenceCount, setFaceAbsenceCount] = useState(0);

  // Load required models
  useEffect(() => {
    const loadModels = async () => {
      try {
        await faceapi.nets.tinyFaceDetector.loadFromUri("/models");
        await faceapi.nets.faceLandmark68Net.loadFromUri("/models");
        await faceapi.nets.faceRecognitionNet.loadFromUri("/models");
        console.log("All required models loaded successfully");
      } catch (error) {
        showAlertWithSound(
          "Failed to load face recognition models. Please check the model files are in the correct path."
        );
      }
    };
    loadModels();
  }, []);

  const getPermissions = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true, audio: true });
      setMediaStream(stream);
      setPermissionsGranted(true);
      setShowPermissionMessage(false);
      initializeSpeechRecognition();
    } catch (error) {
      showAlertWithSound("Please allow access to camera and microphone.");
      setShowPermissionMessage(true);
    }
  };

  const initializeSpeechRecognition = () => {
    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
    if (!SpeechRecognition) {
      console.error("Speech Recognition API not supported in this browser");
      return;
    }
    recognitionRef.current = new SpeechRecognition();
    recognitionRef.current.continuous = true;
    recognitionRef.current.interimResults = false;
    recognitionRef.current.lang = "ar-SA"; // Arabic speech input
    recognitionRef.current.onresult = (event) => {
      const last = event.results.length - 1;
      const text = event.results[last][0].transcript;
      console.log("Recognized Text:", text);
      setTranscript(text);
      checkForbiddenWords(text);
    };
    recognitionRef.current.onerror = (event) => {
      console.error("Speech recognition error", event.error);
    };
    recognitionRef.current.start();
    recognitionRef.current.onend = () => {
      recognitionRef.current.start();
    };
  };

  const checkForbiddenWords = (text) => {
    forbiddenWords.forEach((word) => {
      if (text.includes(word)) {
        if (!detectedForbiddenWords.current.has(word)) {
          detectedForbiddenWords.current.add(word);
          const newCount = detectedForbiddenWords.current.size;
          setForbiddenCount(newCount);
          console.log(`New forbidden word detected: ${word} (Total: ${newCount})`);
          if (newCount >= 3) {
            showAlertWithSound("âš ï¸ Detected 3 different forbidden words! Exam will be terminated.");
            terminateExam();
          }
        }
      }
    });
  };

  const terminateExam = () => {
    setExamTerminated(true);
    showAlertWithSound("âœ– Exam has been terminated due to multiple violations!");
    window.location.replace("/exam-ended"); // â† Ø¥Ø¹Ø§Ø¯Ø© ØªÙˆØ¬ÙŠÙ‡ Ø¨Ø¯Ù„Ø§Ù‹ Ù…Ù† window.close()
  };

  const detectFace = async () => {
    if (webcamRef.current && webcamRef.current.video.readyState === 4) {
      const video = webcamRef.current.video;
      const detections = await faceapi.detectAllFaces(video, new faceapi.TinyFaceDetectorOptions());
      const isFacePresent = detections.length > 0;
      setFaceDetected(isFacePresent);
      if (!isFacePresent) {
        setNoFaceDuration((prev) => prev + 1);
      } else {
        setNoFaceDuration(0);
        setFaceAbsenceCount(0); // Reset absence counter when face returns
      }
    }
  };

  useEffect(() => {
    if (permissionsGranted) {
      const interval = setInterval(detectFace, 1000);
      return () => clearInterval(interval);
    }
  }, [permissionsGranted]);

  useEffect(() => {
    if (noFaceDuration >= 30) {
      setFaceAbsenceCount((prev) => {
        const count = prev + 1;
        if (count === 4) {
          showAlertWithSound(
            "âš  You have been absent for 30 seconds four times. Next time you will be removed from the exam."
          );
        } else if (count >= 5) {
          showAlertWithSound(
            "âœ– You have been absent for 30 seconds five times. Exam will be terminated."
          );
          terminateExam();
        } else {
          showAlertWithSound(
            `âš  Face not detected for 30 seconds. This is your ${count}th absence.`
          );
        }
        return count;
      });
      setNoFaceDuration(0);
    }
  }, [noFaceDuration]);

  const detectSound = useCallback(() => {
    if (!mediaStream) return;
    const audioContext = new AudioContext();
    const analyser = audioContext.createAnalyser();
    const source = audioContext.createMediaStreamSource(mediaStream);
    source.connect(analyser);
    analyser.fftSize = 256;
    const bufferLength = analyser.frequencyBinCount;
    const dataArray = new Uint8Array(bufferLength);
    const checkSound = () => {
      analyser.getByteFrequencyData(dataArray);
      const volume = dataArray.reduce((sum, value) => sum + value, 0) / bufferLength;
      setSoundDetected(volume > 10);
      requestAnimationFrame(checkSound);
    };
    checkSound();
  }, [mediaStream]);

  useEffect(() => {
    if (permissionsGranted) {
      detectSound();
    }
  }, [permissionsGranted, detectSound]);

  const captureAndSendImage = async () => {
    if (!webcamRef.current) return;
    const imageSrc = webcamRef.current.getScreenshot();
    setCurrentImage(imageSrc);
    try {
      const img = await faceapi.fetchImage(imageSrc);
      const detection = await faceapi
        .detectSingleFace(img, new faceapi.TinyFaceDetectorOptions())
        .withFaceLandmarks()
        .withFaceDescriptor();
      if (!detection) return;
      if (!referenceDescriptor) {
        setReferenceDescriptor(detection.descriptor);
        console.log("Reference face stored");
        return;
      }
      const distance = faceapi.euclideanDistance(referenceDescriptor, detection.descriptor);
      if (distance > 0.6) {
        showAlertWithSound("âš ï¸ Different person detected!");
        const violationData = {
          studentId: "student_123",
          examId: "exam_456",
          image: imageSrc,
          timestamp: new Date().toISOString(),
        };
        try {
          await axios.post("http://localhost:5000/api/violation", violationData);
          setViolationCount((prev) => prev + 1);
          setCapturedImages((prev) => [...prev, violationData]);
          if (violationCount + 1 >= 3) {
            showAlertWithSound("âœ– Exam terminated due to multiple violations.");
            terminateExam();
          }
        } catch (err) {
          console.error("Error sending violation to server", err);
        }
      }
    } catch (err) {
      console.error("Error analyzing face", err);
    }
  };

  useEffect(() => {
    if (permissionsGranted && !examTerminated) {
      const interval = setInterval(captureAndSendImage, 60000);
      return () => clearInterval(interval);
    }
  }, [permissionsGranted, examTerminated]);

  return (
    <div className="continuous-auth-container">
      <h2>Exam Proctoring System</h2>
      {showPermissionMessage && (
        <div className="permission-message">
          <h3>Exam Proctoring Setup</h3>
          <p>This exam requires camera and microphone access for proctoring purposes.</p>
          <div className="permission-requirements">
            <p>âœ“ Face detection must be enabled</p>
            <p>âœ“ Microphone must be active</p>
          </div>
          <button className="permission-button" onClick={getPermissions}>
            Enable Camera & Microphone
          </button>
        </div>
      )}
      {permissionsGranted && !examTerminated && (
        <div className="monitoring-container">
          {/* Video Section */}
          <div className="video-section">
            <Webcam
              audio={true}
              ref={webcamRef}
              screenshotFormat="image/jpeg"
              className="webcam"
              videoConstraints={{
                facingMode: "user",
                width: 480,
                height: 360,
              }}
            />
            <div className="status-indicators">
              <div className={`status-indicator ${faceDetected ? "active" : ""}`}>
                {faceDetected ? "Face Detected" : "No Face Detected"}
              </div>
              <div className={`status-indicator ${soundDetected ? "active" : ""}`}>
                {soundDetected ? "Sound Detected" : "No Sound Detected"}
              </div>
            </div>
            {/* Last Captured Image */}
            {currentImage && (
              <div className="captured-image-section">
                <h4>Captured Image</h4>
                <img src={currentImage} alt="Last captured face" className="captured-image" />
              </div>
            )}
          </div>
          {/* Transcript Section */}
          <div className="transcript-section">
            <div className="transcript-box">
              <div className="transcript-header">
                <span>Arabic Speech Transcript</span>
                <span className="violation-counter">Total Forbidden Words: {forbiddenCount}/3</span>
              </div>
              <div className="transcript-content">
                {transcript || "Waiting for audio input..."}
              </div>
            </div>
            {/* All Violation Images */}
            {capturedImages.length > 0 && (
              <div className="all-captured-images">
                <h4>All Violation Captures</h4>
                <div className="captured-images-grid">
                  {capturedImages.map((imgObj, index) => (
                    <img
                      key={index}
                      src={imgObj.image}
                      alt={`Violation ${index}`}
                      className="captured-image-small"
                    />
                  ))}
                </div>
              </div>
            )}
          </div>
        </div>
      )}
      {/* Exam Terminated Message */}
      {examTerminated && (
        <div className="termination-message">
          <h3>âœ– Exam Terminated</h3>
          <p>Due to multiple violations detected. Please contact your instructor.</p>
        </div>
      )}
    </div>
  );
};

export default ContinuousAuth;
