import React, { useState, useEffect, useRef, useCallback } from "react";
import * as faceapi from "face-api.js";
import Webcam from "react-webcam";
import axios from "axios";
import "./ContinuousAuth.css";

const ContinuousAuth = () => {
  const webcamRef = useRef(null);
  const [faceDetected, setFaceDetected] = useState(false);
  const [soundDetected, setSoundDetected] = useState(false);
  const [currentImage, setCurrentImage] = useState(null);
  const [permissionsGranted, setPermissionsGranted] = useState(false);
  const [examTerminated, setExamTerminated] = useState(false);
  const [mediaStream, setMediaStream] = useState(null);
  const [showPermissionMessage, setShowPermissionMessage] = useState(true);
  const [noFaceDuration, setNoFaceDuration] = useState(0);
  const [currentTranscript, setCurrentTranscript] = useState("");
  const recognitionRef = useRef(null);

  useEffect(() => {
    const loadModels = async () => {
      await faceapi.nets.tinyFaceDetector.loadFromUri("/models");
      console.log("Face Detection Model Loaded");
    };
    loadModels();
  }, []);

  const getPermissions = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true, audio: true });
      setMediaStream(stream);
      setPermissionsGranted(true);
      setShowPermissionMessage(false);
      fetchIpAddress();
      initializeSpeechRecognition();
    } catch (error) {
      alert("ÿßŸÑÿ±ÿ¨ÿßÿ° ÿßŸÑÿ≥ŸÖÿßÿ≠ ÿ®ÿßŸÑŸàÿµŸàŸÑ ÿ•ŸÑŸâ ÿßŸÑŸÉÿßŸÖŸäÿ±ÿß ŸàÿßŸÑŸÖŸäŸÉÿ±ŸàŸÅŸàŸÜ.");
      setShowPermissionMessage(true);
    }
  };

  const initializeSpeechRecognition = () => {
    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
    if (!SpeechRecognition) {
      console.error("API ÿßŸÑÿ™ÿπÿ±ŸÅ ÿπŸÑŸâ ÿßŸÑÿµŸàÿ™ ÿ∫Ÿäÿ± ŸÖÿØÿπŸàŸÖ ŸÅŸä Ÿáÿ∞ÿß ÿßŸÑŸÖÿ™ÿµŸÅÿ≠");
      return;
    }

    recognitionRef.current = new SpeechRecognition();
    recognitionRef.current.continuous = true;
    recognitionRef.current.interimResults = true;
    recognitionRef.current.lang = "ar-SA"; // ÿßŸÑŸÑÿ∫ÿ© ÿßŸÑÿπÿ±ÿ®Ÿäÿ© ŸÑŸÑŸÖŸÖŸÑŸÉÿ© ÿßŸÑÿπÿ±ÿ®Ÿäÿ© ÿßŸÑÿ≥ÿπŸàÿØŸäÿ©

    recognitionRef.current.onresult = (event) => {
      let interimTranscript = "";
      
      for (let i = event.resultIndex; i < event.results.length; i++) {
        const transcript = event.results[i][0].transcript;
        if (!event.results[i].isFinal) {
          interimTranscript = transcript;
        } else {
          interimTranscript = transcript;
          // ÿ•ÿπÿßÿØÿ© ÿßŸÑÿ™ÿ¥ÿ∫ŸäŸÑ ÿ®ÿπÿØ ÿßŸÜÿ™Ÿáÿßÿ° ÿßŸÑÿ¨ŸÖŸÑÿ©
          setTimeout(() => {
            setCurrentTranscript("");
          }, 3000);
        }
      }

      setCurrentTranscript(interimTranscript);
    };

    recognitionRef.current.onerror = (event) => {
      console.error("ÿÆÿ∑ÿ£ ŸÅŸä ÿßŸÑÿ™ÿπÿ±ŸÅ ÿπŸÑŸâ ÿßŸÑÿµŸàÿ™", event.error);
    };

    recognitionRef.current.start();
    recognitionRef.current.onend = () => {
      recognitionRef.current.start();
    };
  };

  const fetchIpAddress = async () => {
    try {
      const response = await fetch("https://api.ipify.org?format=json");
      const data = await response.json();
      alert(`ÿπŸÜŸàÿßŸÜ IP ÿßŸÑÿÆÿßÿµ ÿ®ŸÉ: ${data.ip}`);
    } catch (error) {
      console.error("ÿÆÿ∑ÿ£ ŸÅŸä ÿ¨ŸÑÿ® ÿπŸÜŸàÿßŸÜ IP:", error);
    }
  };

  useEffect(() => {
    if (mediaStream) {
      const tracks = mediaStream.getTracks();
      tracks.forEach((track) => {
        track.enabled = true;
      });
    }

    return () => {
      if (recognitionRef.current) {
        recognitionRef.current.stop();
      }
    };
  }, [mediaStream]);

  const detectFace = async () => {
    if (webcamRef.current && webcamRef.current.video.readyState === 4) {
      const video = webcamRef.current.video;
      const detections = await faceapi.detectAllFaces(video, new faceapi.TinyFaceDetectorOptions());
      const isFacePresent = detections.length > 0;
      setFaceDetected(isFacePresent);
      setNoFaceDuration((prev) => (isFacePresent ? 0 : prev + 1));
    }
  };

  useEffect(() => {
    if (permissionsGranted) {
      const interval = setInterval(detectFace, 1000);
      return () => clearInterval(interval);
    }
  }, [permissionsGranted]);

  useEffect(() => {
    if (noFaceDuration >= 15) {
      alert("‚ö†Ô∏è ŸÑŸÖ Ÿäÿ™ŸÖ ÿßŸÉÿ™ÿ¥ÿßŸÅ ÿßŸÑŸàÿ¨Ÿá ŸÑŸÖÿØÿ© 15 ÿ´ÿßŸÜŸäÿ©. ÿßŸÑÿ±ÿ¨ÿßÿ° ÿßŸÑÿ™ÿ£ŸÉÿØ ŸÖŸÜ ÿ∏ŸáŸàÿ± Ÿàÿ¨ŸáŸÉ ÿ®Ÿàÿ∂Ÿàÿ≠.");
      setNoFaceDuration(0);
    }
  }, [noFaceDuration]);

  const detectSound = useCallback(() => {
    if (!mediaStream) return;

    const audioContext = new AudioContext();
    const analyser = audioContext.createAnalyser();
    const source = audioContext.createMediaStreamSource(mediaStream);
    source.connect(analyser);
    analyser.fftSize = 256;
    const bufferLength = analyser.frequencyBinCount;
    const dataArray = new Uint8Array(bufferLength);

    const checkSound = () => {
      analyser.getByteFrequencyData(dataArray);
      const volume = dataArray.reduce((sum, value) => sum + value, 0) / bufferLength;
      setSoundDetected(volume > 10);
      requestAnimationFrame(checkSound);
    };
    checkSound();
  }, [mediaStream]);

  useEffect(() => {
    if (permissionsGranted) {
      detectSound();
    }
  }, [permissionsGranted, detectSound]);

  return (
    <div className="continuous-auth-container">
      <h2>ÿßŸÑŸÖÿµÿßÿØŸÇÿ© ÿßŸÑŸÖÿ≥ÿ™ŸÖÿ±ÿ©</h2>

      {showPermissionMessage && (
        <div className="permission-message">
          <p>ÿßŸÑÿ±ÿ¨ÿßÿ° ÿßŸÑÿ≥ŸÖÿßÿ≠ ÿ®ÿßŸÑŸàÿµŸàŸÑ ÿ•ŸÑŸâ ÿßŸÑŸÉÿßŸÖŸäÿ±ÿß ŸàÿßŸÑŸÖŸäŸÉÿ±ŸàŸÅŸàŸÜ ŸÑÿ®ÿØÿ° ÿßŸÑÿßÿÆÿ™ÿ®ÿßÿ±.</p>
          <button onClick={getPermissions}>ÿßŸÑÿ≥ŸÖÿßÿ≠ ÿ®ÿßŸÑŸÉÿßŸÖŸäÿ±ÿß ŸàÿßŸÑŸÖŸäŸÉÿ±ŸàŸÅŸàŸÜ</button>
        </div>
      )}

      {permissionsGranted ? (
        <>
          <Webcam
            audio={true}
            ref={webcamRef}
            screenshotFormat="image/jpeg"
            className="webcam"
            videoConstraints={{
              facingMode: "user",
              width: 720,
              height: 400,
            }}
          />
          <p>ÿßŸÑŸÉÿßŸÖŸäÿ±ÿß ŸàÿßŸÑŸÖŸäŸÉÿ±ŸàŸÅŸàŸÜ ŸÜÿ¥ÿ∑ŸäŸÜ ŸÑŸÑŸÖÿ±ÿßŸÇÿ®ÿ©.</p>

          <div className="status-buttons-container">
            <div className="status-buttons">
              <button className={faceDetected ? "detected-btn" : "not-detected-btn"}>
                {faceDetected ? "ÿ™ŸÖ ÿßŸÉÿ™ÿ¥ÿßŸÅ ÿßŸÑŸàÿ¨Ÿá ‚úÖ" : "ŸÑŸÖ Ÿäÿ™ŸÖ ÿßŸÉÿ™ÿ¥ÿßŸÅ ÿßŸÑŸàÿ¨Ÿá ‚ùå"}
              </button>
              <button className={soundDetected ? "detected-btn" : "not-detected-btn"}>
                {soundDetected ? "ÿ™ŸÖ ÿßŸÉÿ™ÿ¥ÿßŸÅ ÿßŸÑÿµŸàÿ™ üîä" : "ŸÑŸÖ Ÿäÿ™ŸÖ ÿßŸÉÿ™ÿ¥ÿßŸÅ ÿßŸÑÿµŸàÿ™ üîá"}
              </button>
            </div>

            <div className="captured-images">
              {currentImage && <img src={currentImage} alt="Captured" className="small-image" />}
            </div>
          </div>

          <div className="transcript-container">
            <h3>ÿßŸÑŸÜÿµ ÿßŸÑŸÖÿ≥ŸÖŸàÿπ:</h3>
            <div className="transcript-text">
              <p className="arabic-text">{currentTranscript || "ÿ¨ÿßÿ±Ÿä ÿßŸÑÿßÿ≥ÿ™ŸÖÿßÿπ..."}</p>
            </div>
          </div>
        </>
      ) : (
        <p>ŸÅŸä ÿßŸÜÿ™ÿ∏ÿßÿ± ÿßŸÑŸàÿµŸàŸÑ ÿ•ŸÑŸâ ÿßŸÑŸÉÿßŸÖŸäÿ±ÿß ŸàÿßŸÑŸÖŸäŸÉÿ±ŸàŸÅŸàŸÜ...</p>
      )}

      {examTerminated && <p className="mismatch-count">ÿ™ŸÖ ÿ•ŸÜŸáÿßÿ° ÿßŸÑÿßÿÆÿ™ÿ®ÿßÿ± ÿ®ÿ≥ÿ®ÿ® 3 ÿ™ŸÜÿßŸÇÿ∂ÿßÿ™.</p>}
    </div>
  );
};

export default ContinuousAuth;
