  const [referenceDescriptor, setReferenceDescriptor] = useState(null); // ‚Üê ÿ≥Ÿäÿ™ŸÖ ÿ™ÿÆÿ≤ŸäŸÜ ÿ®ÿµŸÖÿ© ÿßŸÑŸàÿ¨Ÿá ÿßŸÑÿ£ŸàŸÑŸâ ŸáŸÜÿß

  const recognitionRef = useRef(null);

  // Custom Alert with Arabic and English
  const customAlert = (messageAr, messageEn) => {
    alert(`${messageAr}\n${messageEn}`);
  };

  // Load required models
  useEffect(() => {
    const loadModels = async () => {
      try {
        await faceapi.nets.tinyFaceDetector.loadFromUri("/models");
        await faceapi.nets.faceLandmark68Net.loadFromUri("/models");
        await faceapi.nets.faceRecognitionNet.loadFromUri("/models");
        console.log("All required models loaded successfully");
      } catch (error) {
        customAlert(
          "ŸÅÿ¥ŸÑ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ŸÜŸÖÿßÿ∞ÿ¨ ÿßŸÑÿ™ÿπÿ±ŸÅ ÿπŸÑŸâ ÿßŸÑŸàÿ¨Ÿá. Ÿäÿ±ÿ¨Ÿâ ÿßŸÑÿ™ÿ£ŸÉÿØ ŸÖŸÜ Ÿàÿ¨ŸàÿØ ŸÖŸÑŸÅÿßÿ™ ÿßŸÑŸÜŸÖŸàÿ∞ÿ¨ ŸÅŸä ÿßŸÑŸÖÿ≥ÿßÿ± ÿßŸÑÿµÿ≠Ÿäÿ≠.",
          "Failed to load face recognition models. Please check the model files are in the correct path."
        );
      }
    };
    loadModels();
  }, []);

  const getPermissions = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true, audio: true });
      setMediaStream(stream);
      setPermissionsGranted(true);
      setShowPermissionMessage(false);
    } catch (error) {
      customAlert(
        "Ÿäÿ±ÿ¨Ÿâ ÿßŸÑÿ≥ŸÖÿßÿ≠ ÿ®ÿßŸÑŸàÿµŸàŸÑ ÿ•ŸÑŸâ ÿßŸÑŸÉÿßŸÖŸäÿ±ÿß ŸàÿßŸÑŸÖŸäŸÉÿ±ŸàŸÅŸàŸÜ.",
        "Please allow access to camera and microphone."
      );
    }
  };

  const detectFace = async () => {
    if (!webcamRef.current || !webcamRef.current.video) return;

    const video = webcamRef.current.video;
    const detections = await faceapi.detectAllFaces(video, new faceapi.TinyFaceDetectorOptions());
    const isFacePresent = detections.length > 0;
    setFaceDetected(isFacePresent);

    if (!isFacePresent) {
      setNoFaceDuration((prev) => prev + 1);
    } else {
      setNoFaceDuration(0);

      // ÿ•ÿ∞ÿß ŸÑŸÖ ŸäŸÉŸÜ ŸáŸÜÿßŸÉ referenceDescriptor ÿ®ÿπÿØÿå ÿÆÿ∞Ÿá ŸÖŸÜ ÿ£ŸàŸÑ ÿµŸàÿ±ÿ©
      if (!referenceDescriptor) {
        const imageSrc = webcamRef.current.getScreenshot();
        const img = await faceapi.fetchImage(imageSrc);
        const detection = await faceapi
          .detectSingleFace(img)
          .withFaceLandmarks()
          .withFaceDescriptor();

        if (detection) {
          setReferenceDescriptor(detection.descriptor);
          console.log("‚úÖ ÿ™ŸÖ ÿ™ÿÆÿ≤ŸäŸÜ ÿ®ÿµŸÖÿ© ÿßŸÑŸàÿ¨Ÿá ÿßŸÑÿ£ŸàŸÑŸâ (enrollment)");
        }
      } else {
        // ŸáŸÜÿß ŸÜÿ®ÿØÿ£ ÿßŸÑŸÖŸÇÿßÿ±ŸÜÿ© ŸÖÿπ ÿ£ŸàŸÑ ÿµŸàÿ±ÿ© ŸÅŸÇÿ∑
        const imageSrc = webcamRef.current.getScreenshot();
        const img = await faceapi.fetchImage(imageSrc);
        const detection = await faceapi
          .detectSingleFace(img)
          .withFaceLandmarks()
          .withFaceDescriptor();

        if (detection) {
          const distance = faceapi.euclideanDistance(detection.descriptor, referenceDescriptor);
          console.log("üìè ŸÖÿ≥ÿßŸÅÿ© ÿßŸÑÿ™ÿ¥ÿßÿ®Ÿá:", distance.toFixed(2));

          if (distance > 0.5) {
            const violationData = {
              studentId: "student_123",
              examId: "exam_456",
              image: imageSrc,
              timestamp: new Date().toISOString(),
            };

            customAlert("‚ö†Ô∏è ÿ™ŸÖ ÿßŸÉÿ™ÿ¥ÿßŸÅ Ÿàÿ¨Ÿá ŸÖÿÆÿ™ŸÑŸÅ!", "‚ö†Ô∏è Different face detected!");
            setViolationCount((prev) => prev + 1);
            setCapturedImages((prev) => [...prev, violationData]);

            try {
              await axios.post("http://localhost:5000/api/violation", violationData);
            } catch (err) {
              console.error("‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿ•ÿ±ÿ≥ÿßŸÑ ÿßŸÑÿßŸÜÿ™ŸáÿßŸÉ ŸÑŸÑÿÆÿßÿØŸÖ", err);
            }

            if (violationCount + 1 >= 3) {
              terminateExam();
            }
          }
        }
      }
    }
  };

  useEffect(() => {
    if (permissionsGranted && !examTerminated) {
      const interval = setInterval(detectFace, 1000); // ‚Üê Ÿäÿ™ŸÖ ÿßŸÑÿ™ÿ¥ÿ∫ŸäŸÑ ŸÉŸÑ ÿ´ÿßŸÜŸäÿ©
      return () => clearInterval(interval);
    }
  }, [permissionsGranted, examTerminated]);

  useEffect(() => {
    if (noFaceDuration >= 30) {
      customAlert(
        "‚ö† ŸÑŸÖ Ÿäÿ™ŸÖ ÿßŸÉÿ™ÿ¥ÿßŸÅ ÿßŸÑŸàÿ¨Ÿá ŸÑŸÖÿØÿ© 30 ÿ´ÿßŸÜŸäÿ©.",
        "‚ö† Face not detected for 30 seconds."
      );
    }
  }, [noFaceDuration]);

  const terminateExam = () => {
    setExamTerminated(true);
    customAlert(
      "‚úñ ÿ™ŸÖ ÿ•ŸÜŸáÿßÿ° ÿßŸÑÿßŸÖÿ™ÿ≠ÿßŸÜ ÿ®ÿ≥ÿ®ÿ® ÿßŸÜÿ™ŸáÿßŸÉÿßÿ™ ŸÖÿ™ÿπÿØÿØÿ©!",
      "‚úñ Exam has been terminated due to multiple violations!"
    );
    window.close();
  };

  const initializeSpeechRecognition = () => {
    if (!("webkitSpeechRecognition" in window)) {
      console.warn("Web Speech API ÿ∫Ÿäÿ± ŸÖÿØÿπŸàŸÖ ŸÅŸä Ÿáÿ∞ÿß ÿßŸÑŸÖÿ™ÿµŸÅÿ≠");
      return;
    }

    const recognition = new window.webkitSpeechRecognition();
    recognition.continuous = true;
    recognition.interimResults = true;
    recognition.lang = "ar-EG";
    recognition.onresult = (event) => {
      let transcript = "";
      for (let i = event.resultIndex; i < event.results.length; ++i) {
        if (event.results[i].isFinal) {
          transcript += event.results[i][0].transcript;
        }
      }
      console.log("ÿ™ŸÖ ÿßŸÑÿ™ÿπÿ±ŸÅ ÿπŸÑŸâ ÿßŸÑŸÜÿµ:", transcript);
      setTranscript(transcript);
      checkForbiddenWords(transcript);
    };
    recognition.onerror = (event) => {
      console.error("ÿÆÿ∑ÿ£ ŸÅŸä ÿßŸÑÿ™ÿπÿ±ŸÅ ÿπŸÑŸâ ÿßŸÑŸÉŸÑÿßŸÖ", event.error);
    };
    recognition.start();
    recognitionRef.current = recognition;
  };

  const checkForbiddenWords = (text) => {
    forbiddenWords.forEach((word) => {
      if (text.includes(word)) {
        if (!detectedForbiddenWords.current.has(word)) {
          detectedForbiddenWords.current.add(word);
          const newCount = detectedForbiddenWords.current.size;
          setForbiddenCount(newCount);

          console.log(`ÿ™ŸÖ ÿßŸÉÿ™ÿ¥ÿßŸÅ ŸÉŸÑŸÖÿ© ŸÖÿ≠ÿ∏Ÿàÿ±ÿ© ÿ¨ÿØŸäÿØÿ©: ${word} (ÿßŸÑŸÖÿ¨ŸÖŸàÿπ: ${newCount})`);
          if (newCount >= 3) {
            customAlert(
              "‚ö†Ô∏è ÿ™ŸÖ ÿßŸÉÿ™ÿ¥ÿßŸÅ 3 ŸÉŸÑŸÖÿßÿ™ ŸÖÿ≠ÿ∏Ÿàÿ±ÿ© ŸÖÿÆÿ™ŸÑŸÅÿ©! ÿ≥Ÿäÿ™ŸÖ ÿ•ŸÜŸáÿßÿ° ÿßŸÑÿßŸÖÿ™ÿ≠ÿßŸÜ.",
              "‚ö†Ô∏è Detected 3 different forbidden words! Exam will be terminated."
            );
            terminateExam();
          }
        }
      }
    });
  };

  const detectSound = useCallback(() => {
    if (!mediaStream) return;
    const audioContext = new AudioContext();
    const analyser = audioContext.createAnalyser();
    const source = audioContext.createMediaStreamSource(mediaStream);
    source.connect(analyser);
    analyser.fftSize = 256;
    const bufferLength = analyser.frequencyBinCount;
    const dataArray = new Uint8Array(bufferLength);
    const checkSound = () => {
      analyser.getByteFrequencyData(dataArray);
      const volume = dataArray.reduce((sum, value) => sum + value, 0) / bufferLength;
      setSoundDetected(volume > 10);
    };
    const soundInterval = setInterval(checkSound, 1000);
    return () => clearInterval(soundInterval);
  }, [mediaStream]);

  useEffect(() => {
    if (permissionsGranted && !examTerminated) {
      initializeSpeechRecognition();
      detectSound();
    }
  }, [permissionsGranted, examTerminated]);

  return (
    <div className="continuous-auth-container">
      <h2>ŸÜÿ∏ÿßŸÖ ÿßŸÑÿ•ÿ¥ÿ±ÿßŸÅ ÿπŸÑŸâ ÿßŸÑÿßŸÖÿ™ÿ≠ÿßŸÜ</h2>

      {showPermissionMessage && (
        <div className="permission-message">
          <h3>ÿ•ÿπÿØÿßÿØ ÿßŸÑÿ•ÿ¥ÿ±ÿßŸÅ ÿπŸÑŸâ ÿßŸÑÿßŸÖÿ™ÿ≠ÿßŸÜ</h3>
          <p>Ÿäÿ™ÿ∑ŸÑÿ® Ÿáÿ∞ÿß ÿßŸÑÿßŸÖÿ™ÿ≠ÿßŸÜ ÿßŸÑŸàÿµŸàŸÑ ÿ•ŸÑŸâ ÿßŸÑŸÉÿßŸÖŸäÿ±ÿß ŸàÿßŸÑŸÖŸäŸÉÿ±ŸàŸÅŸàŸÜ ŸÑÿ£ÿ∫ÿ±ÿßÿ∂ ÿßŸÑÿ•ÿ¥ÿ±ÿßŸÅ.</p>
          <div className="permission-requirements">
            <p>‚úì Ÿäÿ¨ÿ® ÿ™ŸÖŸÉŸäŸÜ ŸÉÿ¥ŸÅ ÿßŸÑŸàÿ¨Ÿá</p>
            <p>‚úì Ÿäÿ¨ÿ® ÿ™ŸÅÿπŸäŸÑ ÿßŸÑŸÖŸäŸÉÿ±ŸàŸÅŸàŸÜ</p>
          </div>
          <button className="permission-button" onClick={getPermissions}>
            ÿ™ŸÖŸÉŸäŸÜ ÿßŸÑŸÉÿßŸÖŸäÿ±ÿß ŸàÿßŸÑŸÖŸäŸÉÿ±ŸàŸÅŸàŸÜ
          </button>
        </div>
      )}

      {permissionsGranted && !examTerminated && (
        <div className="monitoring-container">
          {/* Video Section */}
          <div className="video-section">
            <Webcam
              audio={true}
              ref={webcamRef}
              screenshotFormat="image/jpeg"
              className="webcam"
              videoConstraints={{
                facingMode: "user",
                width: 480,
                height: 360,
              }}
            />
            <div className="status-indicators">
              <div className={`status-indicator ${faceDetected ? "active" : ""}`}>
                <span>.Face Detection</span>
              </div>
              <span className="violation-counter">ÿπÿØÿØ ÿßŸÑÿßŸÜÿ™ŸáÿßŸÉÿßÿ™: {violationCount}/3</span>
            </div>
          </div>

          <div className="audio-section">
            <h4>ÿßŸÑŸÉŸÑŸÖÿßÿ™ ÿßŸÑŸÖÿ≠ÿ∏Ÿàÿ±ÿ©</h4>
            <span className="violation-counter">ÿπÿØÿØ ÿßŸÑŸÉŸÑŸÖÿßÿ™ ÿßŸÑŸÖÿ≠ÿ∏Ÿàÿ±ÿ©: {forbiddenCount}/3</span>
            <div className="transcript-content" dir="rtl">{transcript || "ŸÅŸä ÿßŸÜÿ™ÿ∏ÿßÿ± ÿ•ÿØÿÆÿßŸÑ ÿßŸÑÿµŸàÿ™..."}</div>
          </div>

          {capturedImages.length > 0 && (
            <div className="all-captured-images">
              <h4>ÿ¨ŸÖŸäÿπ ÿßŸÑÿµŸàÿ± ÿßŸÑÿ™Ÿä ÿ™ÿ≠ÿ™ŸàŸä ÿπŸÑŸâ ÿßŸÜÿ™ŸáÿßŸÉÿßÿ™</h4>
              <div className="captured-images-grid">
                {capturedImages.map((imgObj, index) => (
                  <img
                    key={index}
                    src={imgObj.image}
                    alt={`Violation ${index}`}
                    className="captured-image-small"
                  />
                ))}
              </div>
            </div>
          )}
        </div>
      )}

      {examTerminated && (
        <div className="termination-message">
          <h3>‚úñ ÿ™ŸÖ ÿ•ŸÜŸáÿßÿ° ÿßŸÑÿßŸÖÿ™ÿ≠ÿßŸÜ</h3>
          <p>ÿ®ÿ≥ÿ®ÿ® ÿßŸÉÿ™ÿ¥ÿßŸÅ ÿßŸÜÿ™ŸáÿßŸÉÿßÿ™ ŸÖÿ™ÿπÿØÿØÿ©. Ÿäÿ±ÿ¨Ÿâ ÿßŸÑÿ™ŸàÿßÿµŸÑ ŸÖÿπ ÿßŸÑŸÖŸèÿØÿ±ŸêŸëÿ≥.</p>
        </div>
      )}
    </div>
  );
};

export default ContinuousAuth;
