import React, { useState, useEffect, useRef, useCallback } from "react";

const VoiceDetection = () => {
  const [multipleVoicesDetected, setMultipleVoicesDetected] = useState(false);
  const [violations, setViolations] = useState(0);
  const [isReady, setIsReady] = useState(false);
  const [status, setStatus] = useState("ÿßŸÜÿ™ÿ∏ÿ± ÿ®ÿØÿ° ÿßŸÑÿßÿÆÿ™ÿ®ÿßÿ±");
  
  const audioContextRef = useRef(null);
  const analyserRef = useRef(null);
  const lastEnergyLevelRef = useRef(0);
  const lastProfileRef = useRef(null);
  const lastTimestampRef = useRef(0);
  const streamRef = useRef(null);

  // ÿ•ŸÜÿ¥ÿßÿ° ÿ®ÿµŸÖÿ© ÿµŸàÿ™Ÿäÿ©
  const createVoiceProfile = () => {
    const bufferLength = analyserRef.current.frequencyBinCount;
    const dataArray = new Float32Array(bufferLength);
    analyserRef.current.getFloatFrequencyData(dataArray);

    const profile = {
      lowRange: 0,    // 85-300Hz
      midRange: 0,    // 300-1000Hz
      highRange: 0,   // 1000-4000Hz
      peakCount: 0,
      totalEnergy: 0
    };

    for (let i = 0; i < bufferLength; i++) {
      const freq = i * audioContextRef.current.sampleRate / analyserRef.current.fftSize;
      const value = Math.pow(10, dataArray[i] / 20);

      if (freq >= 85 && freq < 300) profile.lowRange += value;
      else if (freq >= 300 && freq < 1000) profile.midRange += value;
      else if (freq >= 1000 && freq < 4000) profile.highRange += value;

      if (dataArray[i] > -40) profile.peakCount++;
      profile.totalEnergy += value;
    }

    return profile;
  };

  // ÿ™ÿ≠ŸÑŸäŸÑ ÿßŸÑÿµŸàÿ™
  const analyzeVoice = useCallback(() => {
    if (!analyserRef.current) return;

    const currentProfile = createVoiceProfile();
    const energyLevel = currentProfile.totalEnergy;
    const energyDifference = Math.abs(energyLevel - lastEnergyLevelRef.current);
    const timeDifference = Date.now() - lastTimestampRef.current;

    // ÿ¥ÿ±Ÿàÿ∑ ÿßŸÑŸÉÿ¥ŸÅ ÿßŸÑŸÖÿ≠ÿ≥ŸÜÿ©
    const isSuddenChange = energyDifference > 15 && timeDifference < 200;
    const isConsistentChange = energyDifference > 8 && timeDifference < 500;
    
    if (isSuddenChange || isConsistentChange) {
      setMultipleVoicesDetected(true);
      setStatus("ÿ™ŸÖ ÿßŸÉÿ™ÿ¥ÿßŸÅ ÿµŸàÿ™ ÿ¨ÿØŸäÿØ!");
      lastEnergyLevelRef.current = energyLevel;
      lastTimestampRef.current = Date.now();
    } else {
      setMultipleVoicesDetected(false);
      setStatus("ÿ¨ÿßÿ±Ÿä ÿßŸÑŸÖÿ±ÿßŸÇÿ®ÿ©...");
    }

    lastProfileRef.current = currentProfile;
    requestAnimationFrame(analyzeVoice);
  }, []);

  // ÿ•ÿπÿØÿßÿØ ÿßŸÑÿ™ÿ≠ŸÑŸäŸÑ ÿßŸÑÿµŸàÿ™Ÿä
  const setupVoiceAnalysis = useCallback((stream) => {
    try {
      audioContextRef.current = new (window.AudioContext || window.webkitAudioContext)();
      analyserRef.current = audioContextRef.current.createAnalyser();
      analyserRef.current.fftSize = 4096;
      
      const source = audioContextRef.current.createMediaStreamSource(stream);
      source.connect(analyserRef.current);
      
      setStatus("ÿ¨ÿßÿ±Ÿä ÿ™ÿ≠ŸÑŸäŸÑ ÿßŸÑÿµŸàÿ™...");
      analyzeVoice();
    } catch (error) {
      console.error("Voice analysis error:", error);
      setStatus("ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ŸÑŸäŸÑ ÿßŸÑÿµŸàÿ™");
    }
  }, [analyzeVoice]);

  // ÿßŸÉÿ™ÿ¥ÿßŸÅ ÿßŸÑÿµŸàÿ™ ÿßŸÑÿ£ÿ≥ÿßÿ≥Ÿä
  const detectSound = useCallback((stream) => {
    if (!stream) return;

    const audioContext = new AudioContext();
    const analyser = audioContext.createAnalyser();
    const source = audioContext.createMediaStreamSource(stream);
    source.connect(analyser);
    analyser.fftSize = 2048;
    
    const bufferLength = analyser.frequencyBinCount;
    const dataArray = new Uint8Array(bufferLength);

    const checkSound = () => {
      analyser.getByteFrequencyData(dataArray);
      const volume = dataArray.reduce((sum, value) => sum + value, 0) / bufferLength;
      
      if (volume > 25) {
        console.log("üîä ÿµŸàÿ™ ÿ™ŸÖ ÿßŸÉÿ™ÿ¥ÿßŸÅŸá - ŸÖÿ≥ÿ™ŸàŸâ ÿßŸÑÿµŸàÿ™:", volume.toFixed(2));
      }
      
      requestAnimationFrame(checkSound);
    };
    
    checkSound();
  }, []);

  // ÿ®ÿØÿ° ÿßŸÑÿßÿÆÿ™ÿ®ÿßÿ±
  const startTest = async () => {
    try {
      setStatus("ÿ¨ÿßÿ±Ÿä ÿ∑ŸÑÿ® ÿ•ÿ∞ŸÜ ÿßŸÑŸÖŸäŸÉÿ±ŸàŸÅŸàŸÜ...");
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      streamRef.current = stream;
      
      setupVoiceAnalysis(stream);
      detectSound(stream);
      setIsReady(true);
    } catch (error) {
      console.error("Error getting media stream:", error);
      setStatus("ŸÅÿ¥ŸÑ ŸÅŸä ÿßŸÑŸàÿµŸàŸÑ ÿ•ŸÑŸâ ÿßŸÑŸÖŸäŸÉÿ±ŸàŸÅŸàŸÜ");
    }
  };

  // ÿßŸÑÿ™ŸàŸÇŸÅ ÿπŸÜ ÿßŸÑÿßÿÆÿ™ÿ®ÿßÿ±
  const stopTest = () => {
    if (streamRef.current) {
      streamRef.current.getTracks().forEach(track => track.stop());
    }
    setIsReady(false);
    setStatus("ÿ™ŸÖ ÿ•ŸäŸÇÿßŸÅ ÿßŸÑÿßÿÆÿ™ÿ®ÿßÿ±");
  };

  // ÿ™ÿ£ÿ´Ÿäÿ± ŸÑŸÑŸÉÿ¥ŸÅ ÿπŸÜ ÿßŸÑÿßŸÜÿ™ŸáÿßŸÉÿßÿ™
  useEffect(() => {
    if (multipleVoicesDetected) {
      const newViolations = violations + 1;
      setViolations(newViolations);

      alert(`üö® ÿßŸÜÿ™ÿ®ÿßŸá! ÿ™ŸÖ ÿßŸÉÿ™ÿ¥ÿßŸÅ ÿµŸàÿ™ ÿ•ÿ∂ÿßŸÅŸä (ÿßŸÜÿ™ŸáÿßŸÉ ${newViolations}/3)`);

      if (newViolations >= 3) {
        alert("‚úñ ÿ™ŸÖ ÿ•ŸÜŸáÿßÿ° ÿßŸÑÿßÿÆÿ™ÿ®ÿßÿ± ÿ®ÿ≥ÿ®ÿ® ÿßŸÉÿ™ÿ¥ÿßŸÅ 3 ÿßŸÜÿ™ŸáÿßŸÉÿßÿ™");
        stopTest();
      }
    }
  }, [multipleVoicesDetected, violations]);

  // ÿ™ŸÜÿ∏ŸäŸÅ ÿßŸÑŸÖŸàÿßÿ±ÿØ
  useEffect(() => {
    return () => {
      if (streamRef.current) {
        streamRef.current.getTracks().forEach(track => track.stop());
      }
    };
  }, []);

  return (
    <div style={{ textAlign: 'center', padding: '20px' }}>
      <h2>ŸÜÿ∏ÿßŸÖ ŸÉÿ¥ŸÅ ÿßŸÑÿ£ÿµŸàÿßÿ™ ÿßŸÑŸÖÿ™ÿπÿØÿØÿ©</h2>
      
      <div style={{ margin: '20px 0', fontSize: '18px', color: multipleVoicesDetected ? 'red' : 'green' }}>
        {status}
      </div>
      
      <div style={{ margin: '20px 0' }}>
        <p>ÿπÿØÿØ ÿßŸÑÿßŸÜÿ™ŸáÿßŸÉÿßÿ™: {violations}/3</p>
      </div>
      
      {!isReady ? (
        <button 
          onClick={startTest}
          style={{ padding: '10px 20px', fontSize: '16px', backgroundColor: '#4CAF50', color: 'white', border: 'none', borderRadius: '5px' }}
        >
          ÿ®ÿØÿ° ÿßŸÑÿßÿÆÿ™ÿ®ÿßÿ±
        </button>
      ) : (
        <button 
          onClick={stopTest}
          style={{ padding: '10px 20px', fontSize: '16px', backgroundColor: '#f44336', color: 'white', border: 'none', borderRadius: '5px' }}
        >
          ÿ•ŸäŸÇÿßŸÅ ÿßŸÑÿßÿÆÿ™ÿ®ÿßÿ±
        </button>
      )}
      
      <div style={{ marginTop: '30px', padding: '15px', backgroundColor: '#f5f5f5', borderRadius: '5px' }}>
        <h3>ÿ™ÿπŸÑŸäŸÖÿßÿ™ ÿßŸÑÿßÿ≥ÿ™ÿÆÿØÿßŸÖ:</h3>
        <ol style={{ textAlign: 'left', marginLeft: '20px' }}>
          <li>ÿßÿ∂ÿ∫ÿ∑ ÿπŸÑŸâ "ÿ®ÿØÿ° ÿßŸÑÿßÿÆÿ™ÿ®ÿßÿ±" ŸÑŸÑÿ≥ŸÖÿßÿ≠ ÿ®ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ ÿßŸÑŸÖŸäŸÉÿ±ŸàŸÅŸàŸÜ</li>
          <li>ÿ™ÿ≠ÿØÿ´ ÿ®ÿ¥ŸÉŸÑ ÿ∑ÿ®ŸäÿπŸä</li>
          <li>ÿ≥Ÿäÿ™ŸÖ ÿßŸÉÿ™ÿ¥ÿßŸÅ ÿ£Ÿä ÿ£ÿµŸàÿßÿ™ ÿ•ÿ∂ÿßŸÅŸäÿ© ÿ™ŸÑŸÇÿßÿ¶ŸäÿßŸã</li>
          <li>3 ÿßŸÜÿ™ŸáÿßŸÉÿßÿ™ ÿ≥ÿ™ÿ§ÿØŸä ŸÑÿ•ŸÜŸáÿßÿ° ÿßŸÑÿßÿÆÿ™ÿ®ÿßÿ±</li>
        </ol>
      </div>
    </div>
  );
};

export default VoiceDetection;
