              hasViolation = true;
              alert(`‚ö† ÿßŸÑŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ≠ÿ∏Ÿàÿ±ÿ© "${word}" ÿ™ŸÖ ÿ™ŸÉÿ±ÿßÿ±Ÿáÿß ÿ£ŸÉÿ´ÿ± ŸÖŸÜ ${maxRepeats} ŸÖÿ±ÿßÿ™.`);
            }
          });

          // ÿ≤ŸäÿßÿØÿ© ÿπÿØÿØ ÿßŸÑÿßŸÜÿ™ŸáÿßŸÉÿßÿ™ ÿ•ÿ∞ÿß ÿ™ŸÖ ÿ™ÿ¨ÿßŸàÿ≤ ÿßŸÑÿ≠ÿØ
          if (hasViolation) {
            setViolations((prevViolations) => prevViolations + detectedWords.size);
          }

          return updatedCounts;
        });

