import React, { useState, useEffect, useRef } from "react";
import * as faceapi from "face-api.js";
import * as tf from "@tensorflow/tfjs";
import * as yamnet from "@tensorflow-models/yamnet";
import Webcam from "react-webcam";
import "./ContinuousAuth.css";

const ContinuousAuth = () => {
  const webcamRef = useRef(null);
  const [faceDetected, setFaceDetected] = useState(false);
  const [currentImage, setCurrentImage] = useState(null);
  const [permissionsGranted, setPermissionsGranted] = useState(false);
  const [examTerminated, setExamTerminated] = useState(false);
  const [mediaStream, setMediaStream] = useState(null);
  const [showPermissionMessage, setShowPermissionMessage] = useState(true);
  const [noFaceDuration, setNoFaceDuration] = useState(0);
  const [transcript, setTranscript] = useState("");
  const [violations, setViolations] = useState(0);
  const recognitionRef = useRef(null);
  const yamnetModelRef = useRef(null);
  const [detectedSounds, setDetectedSounds] = useState([]);
  const [suspiciousActivity, setSuspiciousActivity] = useState(false);

  // ŸÇÿßÿ¶ŸÖÿ© ÿ®ÿ£ÿµŸàÿßÿ™ ŸÖÿ¥ÿ®ŸàŸáÿ© (ŸäŸÖŸÉŸÜ ÿ™Ÿàÿ≥ŸäÿπŸáÿß ÿ≠ÿ≥ÿ® ÿßŸÑÿ≠ÿßÿ¨ÿ©)
  const SUSPICIOUS_SOUNDS = [
    "Speech", "Whistling", "Whispering", "Conversation", 
    "Typing", "Computer keyboard", "Mouse click", "Alarm"
  ];

  useEffect(() => {
    const loadModels = async () => {
      await Promise.all([
        faceapi.nets.tinyFaceDetector.loadFromUri("/models"),
        tf.ready(),
        loadYamnetModel()
      ]);
      console.log("All models loaded");
    };
    loadModels();
  }, []);

  const loadYamnetModel = async () => {
    try {
      yamnetModelRef.current = await yamnet.load();
      console.log("YAMNet model loaded");
    } catch (error) {
      console.error("Failed to load YAMNet model", error);
    }
  };

  const getPermissions = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ 
        video: true, 
        audio: {
          echoCancellation: false,
          noiseSuppression: false,
          autoGainControl: false
        }
      });
      setMediaStream(stream);
      setPermissionsGranted(true);
      setShowPermissionMessage(false);
      initializeSpeechRecognition();
      startSoundAnalysis(stream);
    } catch (error) {
      alert("Please allow access to camera and microphone.");
      setShowPermissionMessage(true);
    }
  };

  const startSoundAnalysis = async (stream) => {
    if (!yamnetModelRef.current) return;

    const audioContext = new (window.AudioContext || window.webkitAudioContext)();
    const source = audioContext.createMediaStreamSource(stream);
    const processor = audioContext.createScriptProcessor(16384, 1, 1);

    source.connect(processor);
    processor.connect(audioContext.destination);

    processor.onaudioprocess = async (e) => {
      if (!yamnetModelRef.current) return;

      const inputData = e.inputBuffer.getChannelData(0);
      const waveform = new Float32Array(inputData.length);
      for (let i = 0; i < inputData.length; i++) {
        waveform[i] = inputData[i];
      }

      try {
        const result = await yamnetModelRef.current.classify(waveform);
        processSoundResults(result);
      } catch (error) {
        console.error("Sound classification error:", error);
      }
    };
  };

  const processSoundResults = (predictions) => {
    const topPredictions = predictions
      .filter(p => p.score > 0.1) // ÿ™ÿ¨ÿßŸáŸÑ ÿßŸÑÿ™ŸàŸÇÿπÿßÿ™ ÿßŸÑÿ∂ÿπŸäŸÅÿ©
      .sort((a, b) => b.score - a.score)
      .slice(0, 3); // ÿ£ÿπŸÑŸâ 3 ÿ™ŸàŸÇÿπÿßÿ™

    setDetectedSounds(topPredictions);

    // ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ Ÿàÿ¨ŸàÿØ ÿ£ÿµŸàÿßÿ™ ŸÖÿ¥ÿ®ŸàŸáÿ©
    const hasSuspiciousSound = topPredictions.some(pred => 
      SUSPICIOUS_SOUNDS.some(sound => pred.className.includes(sound))
    );

    setSuspiciousActivity(hasSuspiciousSound);

    // ÿ≤ŸäÿßÿØÿ© ÿπÿØÿØ ÿßŸÑŸÖÿÆÿßŸÑŸÅÿßÿ™ ÿ•ÿ∞ÿß ÿ™ŸÖ ÿßŸÉÿ™ÿ¥ÿßŸÅ ÿ£ÿµŸàÿßÿ™ ŸÖÿ¥ÿ®ŸàŸáÿ©
    if (hasSuspiciousSound) {
      const newViolations = violations + 1;
      setViolations(newViolations);
      
      if (newViolations >= 3) {
        setExamTerminated(true);
      }
    }
  };

  const initializeSpeechRecognition = () => {
    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
    if (!SpeechRecognition) {
      console.error("Speech Recognition API not supported in this browser");
      return;
    }

    recognitionRef.current = new SpeechRecognition();
    recognitionRef.current.continuous = true;
    recognitionRef.current.interimResults = false;
    recognitionRef.current.lang = "ar-SA";

    recognitionRef.current.onresult = (event) => {
      const last = event.results.length - 1;
      const text = event.results[last][0].transcript;
      setTranscript(text);
    };

    recognitionRef.current.onerror = (event) => {
      console.error("Speech recognition error", event.error);
    };

    recognitionRef.current.start();
    recognitionRef.current.onend = () => {
      recognitionRef.current.start();
    };
  };

  useEffect(() => {
    if (mediaStream) {
      const tracks = mediaStream.getTracks();
      tracks.forEach((track) => {
        track.enabled = true;
      });
    }

    return () => {
      if (recognitionRef.current) {
        recognitionRef.current.stop();
      }
      if (mediaStream) {
        mediaStream.getTracks().forEach(track => track.stop());
      }
    };
  }, [mediaStream]);

  const detectFace = async () => {
    if (webcamRef.current && webcamRef.current.video.readyState === 4) {
      const video = webcamRef.current.video;
      const detections = await faceapi.detectAllFaces(video, new faceapi.TinyFaceDetectorOptions());
      const isFacePresent = detections.length > 0;
      setFaceDetected(isFacePresent);
      setNoFaceDuration((prev) => (isFacePresent ? 0 : prev + 1));
    }
  };

  useEffect(() => {
    if (permissionsGranted) {
      const interval = setInterval(detectFace, 1000);
      return () => clearInterval(interval);
    }
  }, [permissionsGranted]);

  useEffect(() => {
    if (noFaceDuration >= 15) {
      alert("‚ö† Face not detected for 15 seconds. Please make sure your face is clearly visible.");
      setNoFaceDuration(0);
    }
  }, [noFaceDuration]);

  useEffect(() => {
    if (suspiciousActivity) {
      alert(`üö® Suspicious sound detected! (Violation ${violations}/3)`);
    }
  }, [suspiciousActivity]);

  return (
    <div className="continuous-auth-container">
      <h2>Exam Proctoring System</h2>

      {showPermissionMessage && (
        <div className="permission-message">
          <h3>Exam Proctoring Setup</h3>
          <p>This exam requires camera and microphone access for proctoring purposes.</p>
          <div className="permission-requirements">
            <p>‚úì Face detection must be enabled</p>
            <p>‚úì Microphone must be active</p>
          </div>
          <button className="permission-button" onClick={getPermissions}>
            Enable Camera & Microphone
          </button>
        </div>
      )}

      {permissionsGranted && !examTerminated && (
        <div className="monitoring-container">
          <div className="video-section">
            <Webcam
              audio={true}
              ref={webcamRef}
              screenshotFormat="image/jpeg"
              className="webcam"
              videoConstraints={{
                facingMode: "user",
                width: 480,
                height: 360,
              }}
            />
            <div className="status-indicators">
              <div className={`status-indicator ${faceDetected ? "active" : ""}`}>
                {faceDetected ? "Face Detected" : "No Face Detected"}
              </div>
              <div className={`status-indicator ${detectedSounds.length > 0 ? "active" : ""}`}>
                {detectedSounds.length > 0 ? "Sound Detected" : "No Sound Detected"}
              </div>
              {suspiciousActivity && (
                <div className="status-indicator warning">
                  Suspicious Sound Detected!
                </div>
              )}
            </div>
          </div>

          <div className="transcript-section">
            <div className="transcript-box">
              <div className="transcript-header">
                <span>Arabic Speech Transcript</span>
                <span className="violation-counter">Violations: {violations}/3</span>
              </div>
              <div className="transcript-content" dir="rtl">
                {transcript || "Waiting for audio input..."}
              </div>
              <div className="sound-detection-results">
                <h4>Detected Sounds:</h4>
                {detectedSounds.length > 0 ? (
                  <ul>
                    {detectedSounds.map((sound, index) => (
                      <li key={index} className={SUSPICIOUS_SOUNDS.some(s => sound.className.includes(s)) ? "suspicious" : ""}>
                        {sound.className} ({Math.round(sound.score * 100)}%)
                      </li>
                    ))}
                  </ul>
                ) : (
                  <p>No sounds detected</p>
                )}
              </div>
            </div>
          </div>
        </div>
      )}

      {examTerminated && (
        <div className="termination-message">
          <h3>‚úñ Exam Terminated</h3>
          <p>Due to multiple violations detected. Please contact your instructor.</p>
          <p>Total violations: {violations}</p>
          <div className="violation-details">
            <h4>Violation Details:</h4>
            <ul>
              {detectedSounds.filter(s => SUSPICIOUS_SOUNDS.some(ss => s.className.includes(ss)))
                .map((sound, index) => (
                  <li key={index}>{sound.className} (Score: {Math.round(sound.score * 100)}%)</li>
                ))}
            </ul>
          </div>
        </div>
      )}
    </div>
  );
};

export default ContinuousAuth;
