  const [examTerminated, setExamTerminated] = useState(false);
  const [mediaStream, setMediaStream] = useState(null);
  const [showPermissionMessage, setShowPermissionMessage] = useState(true);
  const [noFaceDuration, setNoFaceDuration] = useState(0);
  const [transcript, setTranscript] = useState("");
  const [violations, setViolations] = useState(0);
  const recognitionRef = useRef(null);

  // Enhanced Audio Monitoring State
  const [audioAnalysis, setAudioAnalysis] = useState({
    volumeLevel: 0,
    voiceConsistency: 100,
    backgroundNoiseLevel: 0,
    audioAnomalies: 0,
    speakingRate: 0,
    pitchVariation: 0,
    lastVoicePrint: null,
    audioContextReady: false
  });
  const audioContextRef = useRef(null);
  const analyserRef = useRef(null);
  const voicePrintIntervalRef = useRef(null);
  const audioCheckIntervalRef = useRef(null);
  useEffect(() => {
    const loadModels = async () => {
      await faceapi.nets.tinyFaceDetector.loadFromUri("/models");
      console.log("Face Detection Model Loaded");
    };
    loadModels();
  }, []);
  const getPermissions = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({
        video: true,
        audio: {
          echoCancellation: false,
          noiseSuppression: false,
          autoGainControl: false
        }
      });
      setMediaStream(stream);
      setPermissionsGranted(true);
      setShowPermissionMessage(false);
      initializeAudioAnalysis(stream);
      initializeSpeechRecognition();
    } catch (error) {
      alert("Please allow access to camera and microphone.");
      setShowPermissionMessage(true);
    }
  };

  // Advanced Audio Analysis Initialization
  const initializeAudioAnalysis = stream => {
    try {
      // Create audio context
      audioContextRef.current = new (window.AudioContext || window.webkitAudioContext)({
        latencyHint: "interactive",
        sampleRate: 44100
      });

      // Create analyser node with higher precision
      analyserRef.current = audioContextRef.current.createAnalyser();
      analyserRef.current.fftSize = 8192;
      analyserRef.current.smoothingTimeConstant = 0.8;

      // Create media stream source
      const source = audioContextRef.current.createMediaStreamSource(stream);
      source.connect(analyserRef.current);

      // Initialize voice print collection
      startVoicePrintCollection();

      // Start continuous audio monitoring
      startAudioMonitoring();
      setAudioAnalysis(prev => ({
        ...prev,
        audioContextReady: true
      }));
    } catch (error) {
      console.error("Audio analysis initialization error:", error);
    }
  };

  // Voice Print Collection
  const startVoicePrintCollection = () => {
    const collectVoicePrint = () => {
      if (!analyserRef.current) return;
      const bufferLength = analyserRef.current.frequencyBinCount;
      const frequencyData = new Float32Array(bufferLength);
      const timeDomainData = new Float32Array(bufferLength);
      analyserRef.current.getFloatFrequencyData(frequencyData);
      analyserRef.current.getFloatTimeDomainData(timeDomainData);

      // Calculate voice characteristics
      const voicePrint = {
        timestamp: Date.now(),
        spectralCentroid: calculateSpectralCentroid(frequencyData),
        spectralFlatness: calculateSpectralFlatness(frequencyData),
        energy: calculateEnergy(timeDomainData),
        zeroCrossingRate: calculateZeroCrossingRate(timeDomainData),
        pitch: estimatePitch(timeDomainData),
        formants: estimateFormants(frequencyData)
      };
      setAudioAnalysis(prev => ({
        ...prev,
        lastVoicePrint: voicePrint
      }));
    };
    voicePrintIntervalRef.current = setInterval(collectVoicePrint, 2000);
  };

  // Continuous Audio Monitoring
  const startAudioMonitoring = () => {
    const monitorAudio = () => {
      if (!analyserRef.current) return;
      const bufferLength = analyserRef.current.frequencyBinCount;
      const frequencyData = new Float32Array(bufferLength);
      const timeDomainData = new Float32Array(bufferLength);
      analyserRef.current.getFloatFrequencyData(frequencyData);
      analyserRef.current.getFloatTimeDomainData(timeDomainData);

      // Calculate current audio metrics
      const currentVolume = calculateVolumeLevel(timeDomainData);
      const currentNoise = calculateBackgroundNoise(frequencyData);
      const currentAnomalies = detectAudioAnomalies(frequencyData);
      const speakingRate = estimateSpeakingRate(timeDomainData);
      const pitchVar = estimatePitchVariation(timeDomainData);

      // Detect multiple speakers
      const multipleSpeakersDetected = detectMultipleSpeakers(frequencyData, timeDomainData);

      // Calculate voice consistency if we have a previous voice print
      let consistency = 100;
      if (audioAnalysis.lastVoicePrint) {
        consistency = calculateVoiceConsistency(audioAnalysis.lastVoicePrint, {
          spectralCentroid: calculateSpectralCentroid(frequencyData),
          spectralFlatness: calculateSpectralFlatness(frequencyData),
          energy: calculateEnergy(timeDomainData)
        });
      }
      setAudioAnalysis(prev => ({
        ...prev,
        volumeLevel: currentVolume,
        backgroundNoiseLevel: currentNoise,
        audioAnomalies: prev.audioAnomalies + currentAnomalies,
        voiceConsistency: consistency,
        speakingRate: speakingRate,
        pitchVariation: pitchVar
      }));

      // Check for potential violations
      checkForAudioViolations(currentVolume, currentNoise, consistency, speakingRate, pitchVar, multipleSpeakersDetected);
    };
    audioCheckIntervalRef.current = setInterval(monitorAudio, 500);
  };

  // Function to detect multiple speakers
  const detectMultipleSpeakers = (frequencyData, timeDomainData) => {
    // Step 1: Calculate spectral features for multiple voice detection
    const spectralFlatness = calculateSpectralFlatness(frequencyData);
    const energy = calculateEnergy(timeDomainData);
    const pitchVariation = estimatePitchVariation(timeDomainData);

    // Step 2: Define thresholds for detecting multiple speakers
    const isHighSpectralFlatness = spectralFlatness > 0.4; // High flatness indicates mixed voices
    const isHighEnergy = energy > 0.3; // High energy may indicate overlapping voices
    const isHighPitchVariation = pitchVariation > 80; // High variation in pitch

    // Step 3: Combine indicators to detect multiple speakers
    return isHighSpectralFlatness && isHighEnergy && isHighPitchVariation;
  };

  // Check for audio violations
  const checkForAudioViolations = (volume, noise, consistency, speakingRate, pitchVar, multipleSpeakersDetected) => {
    // Check for multiple speakers based on various indicators
    const violationDetected = consistency < 60 ||
    // Voice characteristics changed significantly
    noise > 0.15 && volume > 0.2 ||
    // High noise with high volume
    speakingRate > 10 ||
    // Unusually fast speech
    pitchVar > 100 ||
    // Extreme pitch variations
    multipleSpeakersDetected; // Multiple speakers detected

    if (violationDetected) {
      setViolations(prev => {
        const newViolations = prev + 1;
        if (newViolations >= 3) {
          setExamTerminated(true);
          clearInterval(voicePrintIntervalRef.current);
          clearInterval(audioCheckIntervalRef.current);
        } else if (newViolations % 1 === 0) {
          alert(`Audio Anomaly Detected! (Violation ${newViolations}/3)\nPlease ensure you're alone in a quiet environment.`);
        }
        return newViolations;
      });
    }
  };

