import React, { useState, useEffect, useRef, useCallback } from "react";
import * as faceapi from "face-api.js";
import Webcam from "react-webcam";
import "./ContinuousAuth.css";

const ContinuousAuth = () => {
  const webcamRef = useRef(null);
  const [faceDetected, setFaceDetected] = useState(false);
  const [currentImage, setCurrentImage] = useState(null);
  const [permissionsGranted, setPermissionsGranted] = useState(false);
  const [examTerminated, setExamTerminated] = useState(false);
  const [mediaStream, setMediaStream] = useState(null);
  const [showPermissionMessage, setShowPermissionMessage] = useState(true);
  const [noFaceDuration, setNoFaceDuration] = useState(0);
  const [transcript, setTranscript] = useState("");
  const [violations, setViolations] = useState(0);
  const recognitionRef = useRef(null);

  // Enhanced Audio Monitoring State
  const [audioAnalysis, setAudioAnalysis] = useState({
    volumeLevel: 0,
    voiceConsistency: 100,
    backgroundNoiseLevel: 0,
    audioAnomalies: 0,
    speakingRate: 0,
    pitchVariation: 0,
    lastVoicePrint: null,
    audioContextReady: false,
  });

  const audioContextRef = useRef(null);
  const analyserRef = useRef(null);
  const voicePrintIntervalRef = useRef(null);
  const audioCheckIntervalRef = useRef(null);

  useEffect(() => {
    const loadModels = async () => {
      await faceapi.nets.tinyFaceDetector.loadFromUri("/models");
      console.log("Face Detection Model Loaded");
    };
    loadModels();
  }, []);

  const getPermissions = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({
        video: true,
        audio: {
          echoCancellation: false,
          noiseSuppression: false,
          autoGainControl: false,
        },
      });
      setMediaStream(stream);
      setPermissionsGranted(true);
      setShowPermissionMessage(false);
      initializeAudioAnalysis(stream);
      initializeSpeechRecognition();
    } catch (error) {
      alert("Please allow access to camera and microphone.");
      setShowPermissionMessage(true);
    }
  };

  // Advanced Audio Analysis Initialization
  const initializeAudioAnalysis = (stream) => {
    try {
      // Create audio context
      audioContextRef.current = new (window.AudioContext || window.webkitAudioContext)({
        latencyHint: "interactive",
        sampleRate: 44100,
      });

      // Create analyser node with higher precision
      analyserRef.current = audioContextRef.current.createAnalyser();
      analyserRef.current.fftSize = 8192;
      analyserRef.current.smoothingTimeConstant = 0.8;

      // Create media stream source
      const source = audioContextRef.current.createMediaStreamSource(stream);
      source.connect(analyserRef.current);

      // Initialize voice print collection
      startVoicePrintCollection();

      // Start continuous audio monitoring
      startAudioMonitoring();

      setAudioAnalysis((prev) => ({
        ...prev,
        audioContextReady: true,
      }));
    } catch (error) {
      console.error("Audio analysis initialization error:", error);
    }
  };

  // Voice Print Collection
  const startVoicePrintCollection = () => {
    const collectVoicePrint = () => {
      if (!analyserRef.current) return;
      const bufferLength = analyserRef.current.frequencyBinCount;
      const frequencyData = new Float32Array(bufferLength);
      const timeDomainData = new Float32Array(bufferLength);
      analyserRef.current.getFloatFrequencyData(frequencyData);
      analyserRef.current.getFloatTimeDomainData(timeDomainData);

      // Calculate voice characteristics
      const voicePrint = {
        timestamp: Date.now(),
        spectralCentroid: calculateSpectralCentroid(frequencyData),
        spectralFlatness: calculateSpectralFlatness(frequencyData),
        energy: calculateEnergy(timeDomainData),
        zeroCrossingRate: calculateZeroCrossingRate(timeDomainData),
        pitch: estimatePitch(timeDomainData),
        formants: estimateFormants(frequencyData),
      };

      setAudioAnalysis((prev) => ({
        ...prev,
        lastVoicePrint: voicePrint,
      }));
    };

    voicePrintIntervalRef.current = setInterval(collectVoicePrint, 2000);
  };

  // Continuous Audio Monitoring
  const startAudioMonitoring = () => {
    const monitorAudio = () => {
      if (!analyserRef.current) return;
      const bufferLength = analyserRef.current.frequencyBinCount;
      const frequencyData = new Float32Array(bufferLength);
      const timeDomainData = new Float32Array(bufferLength);
      analyserRef.current.getFloatFrequencyData(frequencyData);
      analyserRef.current.getFloatTimeDomainData(timeDomainData);

      // Calculate current audio metrics
      const currentVolume = calculateVolumeLevel(timeDomainData);
      const currentNoise = calculateBackgroundNoise(frequencyData);
      const currentAnomalies = detectAudioAnomalies(frequencyData);
      const speakingRate = estimateSpeakingRate(timeDomainData);
      const pitchVar = estimatePitchVariation(timeDomainData);

      // Detect multiple speakers
      const multipleSpeakersDetected = detectMultipleSpeakers(frequencyData, timeDomainData);

      // Calculate voice consistency if we have a previous voice print
      let consistency = 100;
      if (audioAnalysis.lastVoicePrint) {
        consistency = calculateVoiceConsistency(
          audioAnalysis.lastVoicePrint,
          {
            spectralCentroid: calculateSpectralCentroid(frequencyData),
            spectralFlatness: calculateSpectralFlatness(frequencyData),
            energy: calculateEnergy(timeDomainData),
          }
        );
      }

      setAudioAnalysis((prev) => ({
        ...prev,
        volumeLevel: currentVolume,
        backgroundNoiseLevel: currentNoise,
        audioAnomalies: prev.audioAnomalies + currentAnomalies,
        voiceConsistency: consistency,
        speakingRate: speakingRate,
        pitchVariation: pitchVar,
      }));

      // Check for potential violations
      checkForAudioViolations(
        currentVolume,
        currentNoise,
        consistency,
        speakingRate,
        pitchVar,
        multipleSpeakersDetected
      );
    };

    audioCheckIntervalRef.current = setInterval(monitorAudio, 500);
  };

  // Function to detect multiple speakers
  const detectMultipleSpeakers = (frequencyData, timeDomainData) => {
    // Step 1: Calculate spectral features for multiple voice detection
    const spectralFlatness = calculateSpectralFlatness(frequencyData);
    const energy = calculateEnergy(timeDomainData);
    const pitchVariation = estimatePitchVariation(timeDomainData);

    // Step 2: Define thresholds for detecting multiple speakers
    const isHighSpectralFlatness = spectralFlatness > 0.4; // High flatness indicates mixed voices
    const isHighEnergy = energy > 0.3; // High energy may indicate overlapping voices
    const isHighPitchVariation = pitchVariation > 80; // High variation in pitch

    // Step 3: Combine indicators to detect multiple speakers
    return isHighSpectralFlatness && isHighEnergy && isHighPitchVariation;
  };

  // Check for audio violations
  const checkForAudioViolations = (
    volume,
    noise,
    consistency,
    speakingRate,
    pitchVar,
    multipleSpeakersDetected
  ) => {
    // Check for multiple speakers based on various indicators
    const violationDetected =
      consistency < 60 || // Voice characteristics changed significantly
      (noise > 0.15 && volume > 0.2) || // High noise with high volume
      speakingRate > 10 || // Unusually fast speech
      pitchVar > 100 || // Extreme pitch variations
      multipleSpeakersDetected; // Multiple speakers detected

    if (violationDetected) {
      setViolations((prev) => {
        const newViolations = prev + 1;
        if (newViolations >= 3) {
          setExamTerminated(true);
          clearInterval(voicePrintIntervalRef.current);
          clearInterval(audioCheckIntervalRef.current);
        } else if (newViolations % 1 === 0) {
          alert(`Audio Anomaly Detected! (Violation ${newViolations}/3)\nPlease ensure you're alone in a quiet environment.`);
        }
        return newViolations;
      });
    }
  };

  // Speech Recognition Initialization
  const initializeSpeechRecognition = () => {
    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
    if (!SpeechRecognition) {
      console.error("Speech Recognition API not supported in this browser");
      return;
    }

    recognitionRef.current = new SpeechRecognition();
    recognitionRef.current.continuous = true;
    recognitionRef.current.interimResults = true;
    recognitionRef.current.lang = "ar-SA";
    recognitionRef.current.maxAlternatives = 3;

    recognitionRef.current.onresult = (event) => {
      let finalTranscript = "";
      let interimTranscript = "";

      for (let i = event.resultIndex; i < event.results.length; i++) {
        const transcript = event.results[i][0].transcript;
        if (event.results[i].isFinal) {
          finalTranscript += transcript + " ";
        } else {
          interimTranscript += transcript;
        }
      }

      setTranscript(finalTranscript || interimTranscript);

      // Additional check for multiple speakers in transcript
      if (finalTranscript.includes(" Ÿà ") || finalTranscript.includes(" ÿ´ŸÖ ")) {
        setViolations((prev) => prev + 0.5); // Partial violation for conjunction words
      }
    };

    recognitionRef.current.onerror = (event) => {
      console.error("Speech recognition error", event.error);
    };

    recognitionRef.current.start();
    recognitionRef.current.onend = () => {
      recognitionRef.current.start();
    };
  };

  // Face Detection
  const detectFace = async () => {
    if (webcamRef.current && webcamRef.current.video.readyState === 4) {
      const video = webcamRef.current.video;
      const detections = await faceapi.detectAllFaces(video, new faceapi.TinyFaceDetectorOptions());
      const isFacePresent = detections.length > 0;
      setFaceDetected(isFacePresent);
      setNoFaceDuration((prev) => (isFacePresent ? 0 : prev + 1));
    }
  };

  // Cleanup on unmount
  useEffect(() => {
    return () => {
      if (recognitionRef.current) {
        recognitionRef.current.stop();
      }
      if (voicePrintIntervalRef.current) {
        clearInterval(voicePrintIntervalRef.current);
      }
      if (audioCheckIntervalRef.current) {
        clearInterval(audioCheckIntervalRef.current);
      }
      if (audioContextRef.current) {
        audioContextRef.current.close();
      }
    };
  }, []);

  useEffect(() => {
    if (permissionsGranted) {
      const interval = setInterval(detectFace, 1000);
      return () => clearInterval(interval);
    }
  }, [permissionsGranted]);

  useEffect(() => {
    if (noFaceDuration >= 15) {
      alert("‚ö† Face not detected for 15 seconds. Please make sure your face is clearly visible.");
      setNoFaceDuration(0);
    }
  }, [noFaceDuration]);

  return (
    <div className="continuous-auth-container">
      <h2>Advanced Exam Proctoring System</h2>
      {showPermissionMessage && (
        <div className="permission-message">
          <h3>Exam Proctoring Setup</h3>
          <p>This exam requires camera and microphone access for proctoring purposes.</p>
          <div className="permission-requirements">
            <p>‚úì Face detection must be enabled</p>
            <p>‚úì Microphone must be active (raw audio preferred)</p>
            <p>‚úì Quiet environment recommended</p>
          </div>
          <button className="permission-button" onClick={getPermissions}>
            Enable Camera & Microphone
          </button>
        </div>
      )}
      {permissionsGranted && !examTerminated && (
        <div className="monitoring-container">
          <div className="video-section">
            <Webcam
              audio={true}
              ref={webcamRef}
              screenshotFormat="image/jpeg"
              className="webcam"
              videoConstraints={{
                facingMode: "user",
                width: 480,
                height: 360,
              }}
            />
            <div className="status-indicators">
              <div className={`status-indicator ${faceDetected ? "active" : ""}`}>
                {faceDetected ? "Face Detected" : "No Face Detected"}
              </div>
              <div className={`status-indicator ${audioAnalysis.volumeLevel > 0.1 ? "active" : ""}`}>
                {audioAnalysis.volumeLevel > 0.1 ? "Audio Active" : "Audio Silent"}
              </div>
              <div className="status-indicator">
                Voice Consistency: {Math.round(audioAnalysis.voiceConsistency)}%
              </div>
            </div>
          </div>
          <div className="audio-analysis-section">
            <h3>Advanced Audio Monitoring</h3>
            <div className="audio-metrics">
              <div className="metric">
                <span className="metric-label">Volume Level:</span>
                <div className="metric-bar-container">
                  <div
                    className="metric-bar"
                    style={{ width: `${Math.min(100, audioAnalysis.volumeLevel * 500)}%` }}
                  ></div>
                </div>
              </div>
              <div className="metric">
                <span className="metric-label">Background Noise:</span>
                <div className="metric-bar-container">
                  <div
                    className="metric-bar noise"
                    style={{ width: `${Math.min(100, audioAnalysis.backgroundNoiseLevel * 500)}%` }}
                  ></div>
                </div>
              </div>
              <div className="metric">
                <span className="metric-label">Voice Consistency:</span>
                <div className="metric-bar-container">
                  <div
                    className="metric-bar consistency"
                    style={{ width: `${audioAnalysis.voiceConsistency}%` }}
                  ></div>
                </div>
              </div>
              <div className="metric">
                <span className="metric-label">Audio Anomalies:</span>
                <span className="metric-value">{audioAnalysis.audioAnomalies}</span>
              </div>
            </div>
          </div>
          <div className="transcript-section">
            <div className="transcript-box">
              <div className="transcript-header">
                <span>Arabic Speech Transcript</span>
                <span className="violation-counter">Violations: {Math.floor(violations)}/3</span>
              </div>
              <div className="transcript-content" dir="rtl">
                {transcript || "Waiting for audio input..."}
              </div>
              <div className="audio-warnings">
                {audioAnalysis.voiceConsistency < 70 && (
                  <div className="warning-message">
                    ‚ö† Voice inconsistency detected. Please ensure you're the only speaker.
                  </div>
                )}
                {audioAnalysis.backgroundNoiseLevel > 0.2 && (
                  <div className="warning-message">
                    ‚ö† High background noise detected. Find a quieter environment.
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      )}
      {examTerminated && (
        <div className="termination-message">
          <h3>‚úñ Exam Terminated</h3>
          <p>Due to multiple audio violations detected. Please contact your instructor.</p>
          <div className="violation-details">
            <p>Total violations: {violations}</p>
            <p>Final voice consistency: {Math.round(audioAnalysis.voiceConsistency)}%</p>
            <p>Audio anomalies detected: {audioAnalysis.audioAnomalies}</p>
          </div>
        </div>
      )}
    </div>
  );
};

// Additional audio analysis helper functions
function calculateSpectralCentroid(frequencyData) {
  let sum = 0;
  let totalMagnitude = 0;
  for (let i = 0; i < frequencyData.length; i++) {
    const magnitude = Math.pow(10, frequencyData[i] / 20);
    sum += i * magnitude;
    totalMagnitude += magnitude;
  }
  return totalMagnitude > 0 ? sum / totalMagnitude : 0;
}

function calculateSpectralFlatness(frequencyData) {
  let product = 1;
  let sum = 0;
  const count = frequencyData.length;
  for (let i = 0; i < count; i++) {
    const value = Math.pow(10, frequencyData[i] / 20);
    product *= value;
    sum += value;
  }
  return sum > 0 ? product / Math.pow(sum / count, count) : 0;
}

function calculateEnergy(timeDomainData) {
  let sum = 0;
  for (let i = 0; i < timeDomainData.length; i++) {
    sum += Math.pow(timeDomainData[i], 2);
  }
  return Math.sqrt(sum / timeDomainData.length);
}

function calculateZeroCrossingRate(timeDomainData) {
  let crossings = 0;
  for (let i = 1; i < timeDomainData.length; i++) {
    if (timeDomainData[i] * timeDomainData[i - 1] < 0) {
      crossings++;
    }
  }
  return crossings / timeDomainData.length;
}

function estimatePitch(timeDomainData) {
  const maxLag = Math.floor(44100 / 60); // Minimum pitch (60Hz)
  const minLag = Math.floor(44100 / 400); // Maximum pitch (400Hz)
  let bestLag = 0;
  let bestCorrelation = -1;

  for (let lag = minLag; lag <= maxLag; lag++) {
    let correlation = 0;
    for (let i = 0; i < timeDomainData.length - lag; i++) {
      correlation += timeDomainData[i] * timeDomainData[i + lag];
    }
    if (correlation > bestCorrelation) {
      bestCorrelation = correlation;
      bestLag = lag;
    }
  }

  return bestLag > 0 ? 44100 / bestLag : 0;
}

function estimateFormants(frequencyData) {
  const formants = [];
  const binSize = 44100 / frequencyData.length;

  for (let i = 1; i < frequencyData.length - 1; i++) {
    const prev = frequencyData[i - 1];
    const curr = frequencyData[i];
    const next = frequencyData[i + 1];

    if (curr > prev && curr > next && curr > -50) {
      formants.push(i * binSize);
      if (formants.length >= 3) break; // Only need first few formants
    }
  }

  return formants;
}

function calculateBandEnergy(frequencyData, lowFreq, highFreq) {
  const binSize = 44100 / frequencyData.length;
  const startBin = Math.floor(lowFreq / binSize);
  const endBin = Math.ceil(highFreq / binSize);
  let energy = 0;

  for (let i = startBin; i <= endBin; i++) {
    energy += Math.pow(10, frequencyData[i] / 20);
  }

  return energy / (endBin - startBin + 1);
}

function estimateSpeakingRate(timeDomainData) {
  let speechFrames = 0;
  const threshold = 0.05;

  for (let i = 0; i < timeDomainData.length; i++) {
    if (Math.abs(timeDomainData[i]) > threshold) {
      speechFrames++;
    }
  }

  return (speechFrames / timeDomainData.length) * 100;
}

function estimatePitchVariation(timeDomainData) {
  const segmentLength = Math.floor(44100 / 100); // 10ms segments
  const pitches = [];

  for (let i = 0; i < timeDomainData.length - segmentLength; i += segmentLength) {
    const segment = timeDomainData.slice(i, i + segmentLength);
    const pitch = estimatePitch(segment);
    if (pitch > 0) pitches.push(pitch);
  }

  if (pitches.length < 2) return 0;

  let variation = 0;
  for (let i = 1; i < pitches.length; i++) {
    variation += Math.abs(pitches[i] - pitches[i - 1]);
  }

  return variation / (pitches.length - 1);
}

export default ContinuousAuth;
