import React, { useState, useEffect, useRef, useCallback } from "react";
import * as faceapi from "face-api.js";
import Webcam from "react-webcam";
import "./ContinuousAuth.css";

const ContinuousAuth = () => {
  const webcamRef = useRef(null);
  const [faceDetected, setFaceDetected] = useState(false);
  const [soundDetected, setSoundDetected] = useState(false);
  const [currentImage, setCurrentImage] = useState(null);
  const [permissionsGranted, setPermissionsGranted] = useState(false);
  const [examTerminated, setExamTerminated] = useState(false);
  const [mediaStream, setMediaStream] = useState(null);
  const [showPermissionMessage, setShowPermissionMessage] = useState(true);
  const [noFaceDuration, setNoFaceDuration] = useState(0);
  const [transcript, setTranscript] = useState("");
  const [violations, setViolations] = useState(0);
  const recognitionRef = useRef(null);
  const forbiddenWords = ["cheat", "answer", "help"]; // ŸÇÿßÿ¶ŸÖÿ© ÿßŸÑŸÉŸÑŸÖÿßÿ™ ÿßŸÑŸÖÿ≠ÿ∏Ÿàÿ±ÿ©

  // ŸÜÿ∏ÿßŸÖ ŸÉÿ¥ŸÅ ÿßŸÑÿ£ÿµŸàÿßÿ™ ÿßŸÑŸÖÿ™ÿπÿØÿØÿ© ÿßŸÑŸÖÿ≠ÿ≥ŸÜ
  const [multipleVoicesDetected, setMultipleVoicesDetected] = useState(false);
  const audioContextRef = useRef(null);
  const analyserRef = useRef(null);
  const voiceProfileRef = useRef({
    baseline: null,
    lastAlert: 0,
    active: false,
  });

  useEffect(() => {
    const loadModels = async () => {
      await faceapi.nets.tinyFaceDetector.loadFromUri("/models");
      console.log("Face Detection Model Loaded");
    };
    loadModels();
  }, []);

  const getPermissions = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true, audio: true });
      setMediaStream(stream);
      setPermissionsGranted(true);
      setShowPermissionMessage(false);
      initializeSpeechRecognition();
      setupVoiceAnalysis(stream);
    } catch (error) {
      alert("Please allow access to camera and microphone.");
      setShowPermissionMessage(true);
    }
  };

  // ŸÜÿ∏ÿßŸÖ ÿ™ÿ≠ŸÑŸäŸÑ ÿßŸÑÿµŸàÿ™ ÿßŸÑŸÖÿ≠ÿ≥ŸÜ
  const setupVoiceAnalysis = (stream) => {
    try {
      audioContextRef.current = new (window.AudioContext || window.webkitAudioContext)();
      analyserRef.current = audioContextRef.current.createAnalyser();
      analyserRef.current.fftSize = 4096;
      const source = audioContextRef.current.createMediaStreamSource(stream);
      source.connect(analyserRef.current);

      // ÿ•ŸÜÿ¥ÿßÿ° ÿ®ÿµŸÖÿ© ÿµŸàÿ™Ÿäÿ© ÿ£ŸàŸÑŸäÿ© ÿ®ÿπÿØ 3 ÿ´ŸàÿßŸÜŸä
      setTimeout(() => {
        voiceProfileRef.current.baseline = createVoiceProfile();
        voiceProfileRef.current.active = true;
      }, 3000);

      const analyzeVoice = () => {
        if (!voiceProfileRef.current.active) {
          requestAnimationFrame(analyzeVoice);
          return;
        }
        const currentProfile = createVoiceProfile();
        const isMultiple = compareVoiceProfiles(voiceProfileRef.current.baseline, currentProfile);
        if (isMultiple && Date.now() - voiceProfileRef.current.lastAlert > 5000) {
          setMultipleVoicesDetected(true);
          voiceProfileRef.current.lastAlert = Date.now();
        } else {
          setMultipleVoicesDetected(false);
        }
        requestAnimationFrame(analyzeVoice);
      };
      analyzeVoice();
    } catch (error) {
      console.error("Voice analysis error:", error);
    }
  };

  const createVoiceProfile = () => {
    const bufferLength = analyserRef.current.frequencyBinCount;
    const dataArray = new Float32Array(bufferLength);
    analyserRef.current.getFloatFrequencyData(dataArray);
    const profile = {
      lowRange: 0, // 85-300Hz
      midRange: 0, // 300-1000Hz
      highRange: 0, // 1000-4000Hz
      peakCount: 0,
      totalEnergy: 0,
    };
    for (let i = 0; i < bufferLength; i++) {
      const freq = (i * audioContextRef.current.sampleRate) / analyserRef.current.fftSize;
      const value = Math.pow(10, dataArray[i] / 20); // Convert dB to linear
      if (freq >= 85 && freq < 300) {
        profile.lowRange += value;
      } else if (freq >= 300 && freq < 1000) {
        profile.midRange += value;
      } else if (freq >= 1000 && freq < 4000) {
        profile.highRange += value;
      }
      if (dataArray[i] > -40) profile.peakCount++;
      profile.totalEnergy += value;
    }
    return profile;
  };

  const compareVoiceProfiles = (baseline, current) => {
    // ÿ≠ÿ≥ÿßÿ® ŸÜÿ≥ÿ®ÿ© ÿßŸÑÿ™ÿ∫Ÿäÿ± ŸÑŸÉŸÑ ŸÜÿ∑ÿßŸÇ ÿ™ÿ±ÿØÿØŸä
    const lowDiff = Math.abs(current.lowRange - baseline.lowRange) / baseline.lowRange;
    const midDiff = Math.abs(current.midRange - baseline.midRange) / baseline.midRange;
    const highDiff = Math.abs(current.highRange - baseline.highRange) / baseline.highRange;
    const energyDiff = Math.abs(current.totalEnergy - baseline.totalEnergy) / baseline.totalEnergy;
    const peakDiff = Math.abs(current.peakCount - baseline.peakCount);

    // ÿ¥ÿ±Ÿàÿ∑ ÿßŸÉÿ™ÿ¥ÿßŸÅ ÿßŸÑÿ£ÿµŸàÿßÿ™ ÿßŸÑŸÖÿ™ÿπÿØÿØÿ©
    return (
      (lowDiff > 0.6 || midDiff > 0.6 || highDiff > 0.6) &&
      energyDiff > 0.4 &&
      peakDiff > 15
    );
  };

  const initializeSpeechRecognition = () => {
    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
    if (!SpeechRecognition) {
      console.error("Speech Recognition API not supported in this browser");
      return;
    }
    recognitionRef.current = new SpeechRecognition();
    recognitionRef.current.continuous = true;
    recognitionRef.current.interimResults = false;
    recognitionRef.current.lang = "ar-SA"; // ÿßŸÑŸÑÿ∫ÿ© ÿßŸÑÿπÿ±ÿ®Ÿäÿ©
  
    recognitionRef.current.onresult = (event) => {
      const last = event.results.length - 1;
      const text = event.results[last][0].transcript.toLowerCase(); // ÿ™ÿ≠ŸàŸäŸÑ ÿßŸÑŸÜÿµ ÿ•ŸÑŸâ ÿ≠ÿ±ŸàŸÅ ÿµÿ∫Ÿäÿ±ÿ©
  
      // ŸÇÿßÿ¶ŸÖÿ© ÿßŸÑŸÉŸÑŸÖÿßÿ™ ÿßŸÑŸÖÿ≠ÿ∏Ÿàÿ±ÿ© ÿ®ÿßŸÑÿπÿ±ÿ®Ÿä
      
      // ÿßŸÑŸÉÿ¥ŸÅ ÿπŸÜ ÿßŸÑŸÉŸÑŸÖÿßÿ™ ÿßŸÑŸÖÿ≠ÿ∏Ÿàÿ±ÿ©
      const detectedForbiddenWord = forbiddenWords.some((word) => text.includes(word));
      if (detectedForbiddenWord) {
        alert(`‚ö† ŸÉŸÑŸÖÿ© ŸÖÿ≠ÿ∏Ÿàÿ±ÿ© ÿ™ŸÖ ÿßŸÉÿ™ÿ¥ÿßŸÅŸáÿß: ${text}`);
        setViolations((prevViolations) => prevViolations + 1);
      }
  
      setTranscript(text); // ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑŸÜÿµ ÿßŸÑŸÖÿ™ÿ≠ŸàŸÑ ŸÖŸÜ ÿßŸÑŸÉŸÑÿßŸÖ
    };
  
    recognitionRef.current.onerror = (event) => {
      console.error("Speech recognition error", event.error);
    };
  
    recognitionRef.current.start();
    recognitionRef.current.onend = () => {
      recognitionRef.current.start();
    };
  };
   

  useEffect(() => {
    if (mediaStream) {
      const tracks = mediaStream.getTracks();
      tracks.forEach((track) => {
        track.enabled = true;
      });
    }
    return () => {
      if (recognitionRef.current) {
        recognitionRef.current.stop();
      }
      if (audioContextRef.current) {
        audioContextRef.current.close();
      }
    };
  }, [mediaStream]);

  const detectFace = async () => {
    if (webcamRef.current && webcamRef.current.video.readyState === 4) {
      const video = webcamRef.current.video;
      const detections = await faceapi.detectAllFaces(video, new faceapi.TinyFaceDetectorOptions());
      const isFacePresent = detections.length > 0;
      setFaceDetected(isFacePresent);
      setNoFaceDuration((prev) => (isFacePresent ? 0 : prev + 1));
    }
  };

  useEffect(() => {
    if (permissionsGranted) {
      const interval = setInterval(detectFace, 1000);
      return () => clearInterval(interval);
    }
  }, [permissionsGranted]);

  useEffect(() => {
    if (noFaceDuration >= 15) {
      alert("‚ö† Face not detected for 15 seconds. Please make sure your face is clearly visible.");
      setNoFaceDuration(0);
    }
  }, [noFaceDuration]);

  const detectSound = useCallback(() => {
    if (!mediaStream) return;
    const audioContext = new AudioContext();
    const analyser = audioContext.createAnalyser();
    const source = audioContext.createMediaStreamSource(mediaStream);
    source.connect(analyser);
    analyser.fftSize = 256;
    const bufferLength = analyser.frequencyBinCount;
    const dataArray = new Uint8Array(bufferLength);

    const checkSound = () => {
      analyser.getByteFrequencyData(dataArray);
      const volume = dataArray.reduce((sum, value) => sum + value, 0) / bufferLength;
      setSoundDetected(volume > 10);
      requestAnimationFrame(checkSound);
    };
    checkSound();
  }, [mediaStream]);

  useEffect(() => {
    if (permissionsGranted) {
      detectSound();
    }
  }, [permissionsGranted, detectSound]);

  useEffect(() => {
    if (multipleVoicesDetected) {
      const newViolations = violations + 1;
      setViolations(newViolations);
      alert(`üö® MULTIPLE VOICES DETECTED! (Violation ${newViolations}/3)`);
      if (newViolations >= 3) {
        setExamTerminated(true);
      }
    }
  }, [multipleVoicesDetected]);

  return (
    <div className="continuous-auth-container">
      <h2>Exam Proctoring System</h2>
      {showPermissionMessage && (
        <div className="permission-message">
          <h3>Exam Proctoring Setup</h3>
          <p>This exam requires camera and microphone access for proctoring purposes.</p>
          <div className="permission-requirements">
            <p>‚úì Face detection must be enabled</p>
            <p>‚úì Microphone must be active</p>
          </div>
          <button className="permission-button" onClick={getPermissions}>
            Enable Camera & Microphone
          </button>
        </div>
      )}
      {permissionsGranted && !examTerminated && (
        <div className="monitoring-container">
          <div className="video-section">
            <Webcam
              audio={true}
              ref={webcamRef}
              screenshotFormat="image/jpeg"
              className="webcam"
              videoConstraints={{
                facingMode: "user",
                width: 480,
                height: 360,
              }}
            />
            <div className="status-indicators">
              <div className={`status-indicator ${faceDetected ? "active" : ""}`}>
                {faceDetected ? "Face Detected" : "No Face Detected"}
              </div>
              <div className={`status-indicator ${soundDetected ? "active" : ""}`}>
                {soundDetected ? "Sound Detected" : "No Sound Detected"}
              </div>
              {multipleVoicesDetected && (
                <div className="status-indicator warning">
                  Multiple Voices Detected!
                </div>
              )}
            </div>
          </div>
          <div className="transcript-section">
            <div className="transcript-box">
              <div className="transcript-header">
                <span>Arabic Speech Transcript</span>
                <span className="violation-counter">Violations: {violations}/3</span>
              </div>
              <div className="transcript-content" dir="rtl">
                {transcript || "Waiting for audio input..."}
              </div>
              <div className={`voice-detection-status ${multipleVoicesDetected ? "multiple" : ""}`}>
                {multipleVoicesDetected 
                  ? "Multiple voices detected!" 
                  : "Voice analysis active"}
              </div>
            </div>
          </div>
        </div>
      )}
      {examTerminated && (
        <div className="termination-message">
          <h3>‚úñ Exam Terminated</h3>
          <p>Due to multiple violations detected. Please contact your instructor.</p>
          <p>Total violations: {violations}</p>
        </div>
      )}
    </div>
  );
};

export default ContinuousAuth;
