  const analyserRef = useRef(null);
  const streamRef = useRef(null);
  const animationIdRef = useRef(null);

  const startListening = async () => {
    try {
      setStatus("ÿ¨ÿßÿ±Ÿä ÿ™ŸáŸäÿ¶ÿ© ÿßŸÑŸÖŸäŸÉÿ±ŸàŸÅŸàŸÜ...");
      
