  useEffect(() => {
    const loadModels = async () => {
      await faceapi.nets.tinyFaceDetector.loadFromUri("/models");
      console.log("Face Detection Model Loaded");
    };
    loadModels();
  }, []);

  // ... (ÿ®ŸÇŸäÿ© ÿßŸÑÿØŸàÿßŸÑ ÿßŸÑŸÖŸàÿ¨ŸàÿØÿ© ŸÖÿ´ŸÑ getPermissions, setupVoiceAnalysis, etc.)

  const initializeSpeechRecognition = () => {
    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
    if (!SpeechRecognition) {
      console.error("Speech Recognition API not supported in this browser");
      return;
    }

    recognitionRef.current = new SpeechRecognition();
    recognitionRef.current.continuous = true;
    recognitionRef.current.interimResults = true;
    recognitionRef.current.lang = "ar-SA";

    recognitionRef.current.onresult = (event) => {
      let interimTranscript = '';
      let finalTranscript = '';

      for (let i = event.resultIndex; i < event.results.length; i++) {
        const transcript = event.results[i][0].transcript;
        if (event.results[i].isFinal) {
          finalTranscript += transcript + ' ';
          checkForbiddenWords(transcript);
        } else {
          interimTranscript += transcript;
        }
      }

      setTranscript(finalTranscript || interimTranscript);
    };

    recognitionRef.current.onerror = (event) => {
      console.error("Speech recognition error", event.error);
    };

    recognitionRef.current.start();
    recognitionRef.current.onend = () => {
      recognitionRef.current.start();
    };
  };

  // ÿØÿßŸÑÿ© ŸÑŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿßŸÑŸÉŸÑŸÖÿßÿ™ ÿßŸÑŸÖŸÖŸÜŸàÿπÿ©
  const checkForbiddenWords = (text) => {
    if (!text || text.trim() === '') return;

    const lowerText = text.toLowerCase();
    let foundWords = [];

    // ÿßŸÑÿ®ÿ≠ÿ´ ÿπŸÜ ÿßŸÑŸÉŸÑŸÖÿßÿ™ ÿßŸÑŸÖŸÖŸÜŸàÿπÿ© ŸÅŸä ÿßŸÑŸÜÿµ
    forbiddenWords.forEach(word => {
      const lowerWord = word.toLowerCase();
      if (lowerText.includes(lowerWord)) {
        // ÿßŸÑÿ™ÿ£ŸÉÿØ ŸÖŸÜ ÿ£ŸÜ ÿßŸÑŸÉŸÑŸÖÿ© ŸÉÿßŸÖŸÑÿ© ŸàŸÑŸäÿ≥ÿ™ ÿ¨ÿ≤ÿ° ŸÖŸÜ ŸÉŸÑŸÖÿ© ÿ£ÿÆÿ±Ÿâ
        const regex = new RegExp(`\\b${lowerWord}\\b`, 'i');
        if (regex.test(text)) {
          foundWords.push(word);
        }
      }
    });

    // ÿ•ÿ∞ÿß Ÿàÿ¨ÿØŸÜÿß ŸÉŸÑŸÖÿßÿ™ ŸÖŸÖŸÜŸàÿπÿ©
    if (foundWords.length > 0) {
      // ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿπÿØÿßÿØ ŸÑŸÉŸÑ ŸÉŸÑŸÖÿ©
      const newCounts = {...forbiddenWordCounts};
      foundWords.forEach(word => {
        newCounts[word] = (newCounts[word] || 0) + 1;
      });
      setForbiddenWordCounts(newCounts);

      // ÿ≠ÿ≥ÿßÿ® ÿπÿØÿØ ÿßŸÑŸÖÿ±ÿßÿ™ ÿßŸÑÿ™Ÿä ÿ™ŸÖ ŸÅŸäŸáÿß ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ ÿßŸÑŸÉŸÑŸÖÿßÿ™ ÿßŸÑŸÖŸÖŸÜŸàÿπÿ©
      const totalForbiddenUses = Object.values(newCounts).reduce((sum, count) => sum + count, 0);
      
      // ÿ•ÿ∏Ÿáÿßÿ± ÿ™ŸÜÿ®ŸäŸá ÿπŸÜÿØ ÿßŸÑŸàÿµŸàŸÑ ÿ•ŸÑŸâ 3 ÿßÿ≥ÿ™ÿÆÿØÿßŸÖÿßÿ™ (ŸÖÿπ ŸÖŸÜÿπ ÿßŸÑÿ™ŸÉÿ±ÿßÿ± ÿßŸÑÿ≥ÿ±Ÿäÿπ)
      const now = Date.now();
      if (totalForbiddenUses >= 3 && now - lastAlertTime > 5000) {
        const newViolations = forbiddenWordViolations + 1;
        setForbiddenWordViolations(newViolations);
        setLastAlertTime(now);
        
        alert(`‚ö† ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ ÿ∫Ÿäÿ± ŸÖÿ≥ŸÖŸàÿ≠: ÿ™ŸÖ ÿßŸÉÿ™ÿ¥ÿßŸÅ ŸÉŸÑŸÖÿßÿ™ ŸÖŸÖŸÜŸàÿπÿ© (${foundWords.join(', ')})\nÿ™ÿ≠ÿ∞Ÿäÿ± ${newViolations}/3`);
        
        // ÿ•ÿ∞ÿß ŸàÿµŸÑÿ™ ÿßŸÑŸÖÿÆÿßŸÑŸÅÿßÿ™ ÿ•ŸÑŸâ 3ÿå Ÿäÿ™ŸÖ ÿ•ŸÜŸáÿßÿ° ÿßŸÑÿßŸÖÿ™ÿ≠ÿßŸÜ
        if (newViolations >= 3) {
          setExamTerminated(true);
          alert("üö® ÿ™ŸÖ ÿ•ŸÜŸáÿßÿ° ÿßŸÑÿßŸÖÿ™ÿ≠ÿßŸÜ ÿ®ÿ≥ÿ®ÿ® ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ ŸÉŸÑŸÖÿßÿ™ ŸÖŸÖŸÜŸàÿπÿ© ÿ®ÿ¥ŸÉŸÑ ŸÖÿ™ŸÉÿ±ÿ±!");
        }
      }
    }
  };

  // ... (ÿ®ŸÇŸäÿ© ÿßŸÑŸÉŸàÿØ ÿßŸÑŸÖŸàÿ¨ŸàÿØ)

  useEffect(() => {
    if (multipleVoicesDetected || forbiddenWordViolations >= 3) {
      const newViolations = violations + 1;
      setViolations(newViolations);
      
      if (multipleVoicesDetected) {
        alert(`üö® MULTIPLE VOICES DETECTED! (Violation ${newViolations}/3)`);
      }
      
      if (newViolations >= 3) {
        setExamTerminated(true);
      }
    }
  }, [multipleVoicesDetected, forbiddenWordViolations]);

  return (
    <div className="continuous-auth-container">
      {/* ... (ŸÜŸÅÿ≥ ÿßŸÑŸÉŸàÿØ ÿßŸÑÿ≥ÿßÿ®ŸÇ ŸÑŸÑŸàÿßÿ¨Ÿáÿ©) */}
    </div>
  );
};

export default ContinuousAuth;
