import React, { useState, useEffect, useRef } from "react";
import * as faceapi from "face-api.js";
import Webcam from "react-webcam";
import * as tf from "@tensorflow/tfjs";
import "./ContinuousAuth.css";

const ContinuousAuth = () => {
  const webcamRef = useRef(null);
  const [faceDetected, setFaceDetected] = useState(false);
  const [permissionsGranted, setPermissionsGranted] = useState(false);
  const [multipleVoicesDetected, setMultipleVoicesDetected] = useState(false);
  const [mediaStream, setMediaStream] = useState(null);
  const [violations, setViolations] = useState(0);
  const [examTerminated, setExamTerminated] = useState(false);

  let yamnetModel = useRef(null);
  let classLabels = useRef([]);

  const yamnetModelUrl = "https://tfhub.dev/google/yamnet/tfjs/1";
  const labelsUrl = "/yamnet_class_map.csv"; // ÿßŸÑŸÖÿ≥ÿßÿ± ÿØÿßÿÆŸÑ ŸÖÿ¨ŸÑÿØ public


  const loadYAMNetModel = async () => {
    yamnetModel.current = await tf.loadGraphModel(yamnetModelUrl, {
      fromTFHub: true,
    });
    console.log("‚úÖ YAMNet model loaded");
  };

  const loadLabels = async () => {
  try {
    const res = await fetch(labelsUrl);
    if (!res.ok) {
      throw new Error("Failed to fetch labels");
    }
    const csv = await res.text();
    classLabels.current = csv
      .split("\n")
      .slice(1)
      .map((row) => {
        const label = row.split(",")[2]?.replace(/"/g, "");
        return label || "Unknown";
      });

    console.log(classLabels.current);
  } catch (error) {
    console.error("Error loading labels:", error);
  }
};

  

  const analyzeAudioWithYAMNet = async (audioBuffer) => {
    if (!yamnetModel.current) return;

    const audioTensor = tf.tensor(audioBuffer, [1, audioBuffer.length]);
    const result = await yamnetModel.current.predict(audioTensor);
    const scores = result.arraySync()[0];

    const topIndices = scores
      .map((score, index) => ({ score, index }))
      .sort((a, b) => b.score - a.score)
      .slice(0, 3);

    const topLabels = topIndices.map(({ index, score }) => ({
      label: classLabels.current[index],
      score: score.toFixed(3),
    }));

    console.log("üéôÔ∏è Detected:", topLabels);

    const speechCount = topLabels.filter((entry) =>
      entry.label.toLowerCase().includes("speech")
    ).length;

    return speechCount > 1;
  };

  const getPermissions = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({
        video: true,
        audio: true,
      });
      setMediaStream(stream);
      setPermissionsGranted(true);
      setupAudioStream(stream);
    } catch (err) {
      alert("Please enable camera and microphone.");
    }
  };

  const setupAudioStream = (stream) => {
    const audioContext = new AudioContext();
    const source = audioContext.createMediaStreamSource(stream);
    const processor = audioContext.createScriptProcessor(2048, 1, 1);

    source.connect(processor);
    processor.connect(audioContext.destination);

    const audioData = [];

    processor.onaudioprocess = async (e) => {
      const input = e.inputBuffer.getChannelData(0);
      audioData.push(...input);

      if (audioData.length > 16000) {
        const isMultiple = await analyzeAudioWithYAMNet(audioData.slice(0, 16000));
        if (isMultiple) {
          setMultipleVoicesDetected(true);
          const newViolations = violations + 1;
          setViolations(newViolations);
          alert(`üö® MULTIPLE VOICES DETECTED! (Violation ${newViolations}/3)`);
          if (newViolations >= 3) setExamTerminated(true);
        } else {
          setMultipleVoicesDetected(false);
        }
        audioData.length = 0;
      }
    };
  };

  useEffect(() => {
    loadYAMNetModel();
    loadLabels();
  }, []);

  useEffect(() => {
    const detectFace = async () => {
      if (webcamRef.current && webcamRef.current.video.readyState === 4) {
        const video = webcamRef.current.video;
        const detections = await faceapi.detectAllFaces(
          video,
          new faceapi.TinyFaceDetectorOptions()
        );
        setFaceDetected(detections.length > 0);
      }
    };

    if (permissionsGranted) {
      const interval = setInterval(detectFace, 1000);
      return () => clearInterval(interval);
    }
  }, [permissionsGranted]);

  return (
    <div className="continuous-auth-container">
      <h2>Exam Proctoring System</h2>
      {!permissionsGranted && (
        <button onClick={getPermissions}>Enable Camera & Microphone</button>
      )}

      {permissionsGranted && !examTerminated && (
        <div>
          <Webcam ref={webcamRef} audio={true} width={480} height={360} />
          <p>{faceDetected ? "‚úÖ Face Detected" : "‚ùå No Face Detected"}</p>
