import React, { useState, useEffect, useRef } from 'react';

const VoiceDetector = () => {
  const [status, setStatus] = useState('ÿßŸÜŸÇÿ± ÿπŸÑŸâ "ÿ®ÿØÿ° ÿßŸÑÿ™ÿ¥ÿ∫ŸäŸÑ" ŸÑÿ®ÿØÿ° ÿßŸÑŸÉÿ¥ŸÅ');
  const [volume, setVolume] = useState(0);
  const [isListening, setIsListening] = useState(false);
  const audioContextRef = useRef(null);
  const analyserRef = useRef(null);
  const dataArrayRef = useRef(null);
  const animationFrameRef = useRef(null);
  const streamRef = useRef(null);

  // ÿØÿßŸÑÿ© ÿ®ÿØÿ° ÿßŸÑŸÉÿ¥ŸÅ ÿπŸÜ ÿßŸÑÿµŸàÿ™
  const startDetection = async () => {
    try {
      setStatus('ÿ¨ÿßÿ±Ÿä ÿ∑ŸÑÿ® ÿ•ÿ∞ŸÜ ÿßŸÑŸÖŸäŸÉÿ±ŸàŸÅŸàŸÜ...');
      
      // 1. ÿ∑ŸÑÿ® ÿ•ÿ∞ŸÜ ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ ÿßŸÑŸÖŸäŸÉÿ±ŸàŸÅŸàŸÜ
      const stream = await navigator.mediaDevices.getUserMedia({ 
        audio: true,
        video: false
      });
      streamRef.current = stream;
      
      // 2. ÿ•ŸÜÿ¥ÿßÿ° ÿ≥ŸäÿßŸÇ ÿßŸÑÿµŸàÿ™
      const AudioContext = window.AudioContext || window.webkitAudioContext;
      audioContextRef.current = new AudioContext();
      
      // 3. ÿ™ŸáŸäÿ¶ÿ© ÿßŸÑŸÖÿ≠ŸÑŸÑ ÿßŸÑÿµŸàÿ™Ÿä
      analyserRef.current = audioContextRef.current.createAnalyser();
      analyserRef.current.fftSize = 256;
      const bufferLength = analyserRef.current.frequencyBinCount;
      dataArrayRef.current = new Uint8Array(bufferLength);
      
      // 4. ÿ™ŸàÿµŸäŸÑ ŸÖÿµÿØÿ± ÿßŸÑÿµŸàÿ™
      const source = audioContextRef.current.createMediaStreamSource(stream);
      source.connect(analyserRef.current);
      
      setStatus('ÿ¨ÿßÿ±Ÿä ÿßŸÑÿßÿ≥ÿ™ŸÖÿßÿπ... ÿßÿ®ÿØÿ£ ÿ®ÿßŸÑÿ™ÿ≠ÿØÿ´');
      setIsListening(true);
      
      // 5. ÿ®ÿØÿ° ÿπŸÖŸÑŸäÿ© ÿßŸÑÿ™ÿ≠ŸÑŸäŸÑ
      processAudio();
      
    } catch (error) {
      console.error('ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£:', error);
      setStatus(`ÿÆÿ∑ÿ£: ${error.message}`);
    }
  };

  // ÿØÿßŸÑÿ© ŸÖÿπÿßŸÑÿ¨ÿ© ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿµŸàÿ™Ÿäÿ©
  const processAudio = () => {
    if (!analyserRef.current || !isListening) return;
    
    // ÿßŸÑÿ≠ÿµŸàŸÑ ÿπŸÑŸâ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ™ÿ±ÿØÿØÿßÿ™
    analyserRef.current.getByteFrequencyData(dataArrayRef.current);
    
    // ÿ≠ÿ≥ÿßÿ® ŸÖÿ™Ÿàÿ≥ÿ∑ ŸÖÿ≥ÿ™ŸàŸâ ÿßŸÑÿµŸàÿ™
    let sum = 0;
    for (const value of dataArrayRef.current) {
      sum += value;
    }
    const averageVolume = Math.round(sum / dataArrayRef.current.length);
    
    setVolume(averageVolume);
    
    // ŸÉÿ¥ŸÅ ÿ•ÿ∞ÿß ŸÉÿßŸÜ ŸáŸÜÿßŸÉ ÿµŸàÿ™ ŸÖÿ≥ŸÖŸàÿπ
    if (averageVolume > 20) {
      console.log(`üîä ÿµŸàÿ™ ŸÖŸÉÿ™ÿ¥ŸÅ! ÿßŸÑŸÖÿ≥ÿ™ŸàŸâ: ${averageVolume}`);
    }
    
    // ÿßŸÑÿßÿ≥ÿ™ŸÖÿ±ÿßÿ± ŸÅŸä ÿßŸÑÿ™ÿ≠ŸÑŸäŸÑ
    animationFrameRef.current = requestAnimationFrame(processAudio);
  };

  // ÿØÿßŸÑÿ© ÿ•ŸäŸÇÿßŸÅ ÿßŸÑŸÉÿ¥ŸÅ
  const stopDetection = () => {
    if (animationFrameRef.current) {
      cancelAnimationFrame(animationFrameRef.current);
    }
    
    if (streamRef.current) {
      streamRef.current.getTracks().forEach(track => track.stop());
    }
    
    if (audioContextRef.current && audioContextRef.current.state !== 'closed') {
      audioContextRef.current.close();
    }
    
    setIsListening(false);
    setVolume(0);
    setStatus('ÿ™ŸÖ ÿßŸÑÿ•ŸäŸÇÿßŸÅ. ÿßŸÜŸÇÿ± ÿπŸÑŸâ "ÿ®ÿØÿ° ÿßŸÑÿ™ÿ¥ÿ∫ŸäŸÑ" ŸÑÿ®ÿØÿ° ÿ¨ÿØŸäÿØ');
  };

  // ÿ™ŸÜÿ∏ŸäŸÅ ÿßŸÑŸÖŸàÿßÿ±ÿØ ÿπŸÜÿØ ÿ•ÿ∫ŸÑÿßŸÇ ÿßŸÑŸÖŸÉŸàŸÜ
  useEffect(() => {
    return () => {
      stopDetection();
    };
  }, []);

  return (
    <div style={{
      maxWidth: '600px',
      margin: '0 auto',
      padding: '20px',
      fontFamily: 'Arial, sans-serif',
      textAlign: 'center',
      direction: 'rtl'
    }}>
      <h1 style={{ color: '#2c3e50' }}>ŸÉÿßÿ¥ŸÅ ÿßŸÑÿµŸàÿ™ ÿßŸÑŸÖÿ™ŸÇÿØŸÖ</h1>
      
      <div style={{
        margin: '30px 0',
        padding: '20px',
        backgroundColor: '#ecf0f1',
        borderRadius: '10px',
        boxShadow: '0 2px 5px rgba(0,0,0,0.1)'
      }}>
        <p style={{ fontSize: '20px', marginBottom: '10px' }}>ÿßŸÑÿ≠ÿßŸÑÿ©: <strong>{status}</strong></p>
        <div style={{
          height: '30px',
          backgroundColor: '#bdc3c7',
          borderRadius: '15px',
          overflow: 'hidden',
          margin: '15px 0'
        }}>
          <div style={{
            width: `${Math.min(100, volume)}%`,
            height: '100%',
            backgroundColor: volume > 20 ? '#e74c3c' : '#2ecc71',
            transition: 'width 0.3s ease'
          }}></div>
        </div>
        <p style={{ fontSize: '18px' }}>ŸÖÿ≥ÿ™ŸàŸâ ÿßŸÑÿµŸàÿ™: <strong>{volume}</strong></p>
      </div>
      
      <div style={{ margin: '25px 0' }}>
        {!isListening ? (
          <button
            onClick={startDetection}
            style={{
              padding: '12px 30px',
              fontSize: '18px',
              backgroundColor: '#27ae60',
              color: 'white',
              border: 'none',
              borderRadius: '5px',
              cursor: 'pointer',
              margin: '0 10px'
            }}
          >
            ÿ®ÿØÿ° ÿßŸÑÿ™ÿ¥ÿ∫ŸäŸÑ
          </button>
        ) : (
          <button
            onClick={stopDetection}
            style={{
              padding: '12px 30px',
              fontSize: '18px',
              backgroundColor: '#c0392b',
              color: 'white',
              border: 'none',
              borderRadius: '5px',
              cursor: 'pointer',
              margin: '0 10px'
            }}
          >
            ÿ•ŸäŸÇÿßŸÅ
          </button>
        )}
      </div>
      
      <div style={{
        marginTop: '40px',
        padding: '20px',
        backgroundColor: '#f8f9fa',
        borderRadius: '8px',
        textAlign: 'right'
      }}>
        <h3 style={{ color: '#34495e', marginTop: '0' }}>ÿ™ÿπŸÑŸäŸÖÿßÿ™ ÿßŸÑÿßÿ≥ÿ™ÿÆÿØÿßŸÖ:</h3>
        <ol style={{ lineHeight: '1.6' }}>
          <li>ÿßÿ∂ÿ∫ÿ∑ ÿπŸÑŸâ ÿ≤ÿ± "ÿ®ÿØÿ° ÿßŸÑÿ™ÿ¥ÿ∫ŸäŸÑ"</li>
          <li>ÿßÿ≥ŸÖÿ≠ ŸÑŸÑŸÖŸàŸÇÿπ ÿ®ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ ÿßŸÑŸÖŸäŸÉÿ±ŸàŸÅŸàŸÜ</li>
          <li>ÿßÿ®ÿØÿ£ ÿ®ÿßŸÑÿ™ÿ≠ÿØÿ´ ÿ£Ÿà ÿ™ÿ¥ÿ∫ŸäŸÑ ÿµŸàÿ™</li>
          <li>ÿ¥ÿßŸáÿØ ŸÖÿ≥ÿ™ŸàŸâ ÿßŸÑÿµŸàÿ™ ÿπŸÑŸâ ÿßŸÑŸÖŸÇŸäÿßÿ≥</li>
          <li>ÿßŸÑŸÑŸàŸÜ ÿßŸÑÿ£ÿ≠ŸÖÿ± ŸäÿπŸÜŸä ÿßŸÉÿ™ÿ¥ÿßŸÅ ÿµŸàÿ™ ÿπÿßŸÑŸä</li>
          <li>ÿßÿ∂ÿ∫ÿ∑ "ÿ•ŸäŸÇÿßŸÅ" ÿπŸÜÿØ ÿßŸÑÿßŸÜÿ™Ÿáÿßÿ°</li>
        </ol>
      </div>
    </div>
  );
};

export default VoiceDetector;
