import React, { useState, useEffect, useRef, useCallback } from "react";
import * as faceapi from "face-api.js";
import Webcam from "react-webcam";
import axios from "axios";
import "./ContinuousAuth.css";

const ContinuousAuth = () => {
  const webcamRef = useRef(null);
  const [faceDetected, setFaceDetected] = useState(false);
  const [soundDetected, setSoundDetected] = useState(false);
  const [currentImage, setCurrentImage] = useState(null);
  const [permissionsGranted, setPermissionsGranted] = useState(false);
  const [examTerminated, setExamTerminated] = useState(false);
  const [mediaStream, setMediaStream] = useState(null);
  const [showPermissionMessage, setShowPermissionMessage] = useState(true);
  const [noFaceDuration, setNoFaceDuration] = useState(0);
  const [ipAddress, setIpAddress] = useState("");

  useEffect(() => {
    const loadModels = async () => {
      await faceapi.nets.tinyFaceDetector.loadFromUri("/models");
      console.log("Face Detection Model Loaded");
    };
    loadModels();

    // Get the IP address when the page loads
    const fetchIpAddress = async () => {
      try {
        const response = await axios.get("https://api.ipify.org?format=json");
        setIpAddress(response.data.ip);
        alert(`Your IP Address is: ${response.data.ip}`); // Show the IP address in alert
      } catch (error) {
        console.error("Error fetching IP address:", error);
      }
    };

    fetchIpAddress();
  }, []);

  const getPermissions = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true, audio: true });
      setMediaStream(stream);
      setPermissionsGranted(true);
      setShowPermissionMessage(false);
    } catch (error) {
      alert("Please allow access to camera and microphone.");
      setShowPermissionMessage(true);
    }
  };

  useEffect(() => {
    if (mediaStream) {
      const tracks = mediaStream.getTracks();
      tracks.forEach((track) => {
        track.enabled = true;
      });
    }
  }, [mediaStream]);

  const detectFace = async () => {
    if (webcamRef.current && webcamRef.current.video.readyState === 4) {
      const video = webcamRef.current.video;
      const detections = await faceapi.detectAllFaces(video, new faceapi.TinyFaceDetectorOptions());
      const isFacePresent = detections.length > 0;
      setFaceDetected(isFacePresent);
      setNoFaceDuration((prev) => (isFacePresent ? 0 : prev + 1));
    }
  };

  useEffect(() => {
    if (permissionsGranted) {
      const interval = setInterval(detectFace, 1000);
      return () => clearInterval(interval);
    }
  }, [permissionsGranted]);

  useEffect(() => {
    if (noFaceDuration >= 15) {
      alert("⚠️ Face not detected for 15 seconds. Please make sure your face is clearly visible.");
      setNoFaceDuration(0);
    }
  }, [noFaceDuration]);

  const detectSound = useCallback(() => {
    if (!mediaStream) return;

    const audioContext = new AudioContext();
    const analyser = audioContext.createAnalyser();
    const source = audioContext.createMediaStreamSource(mediaStream);
    source.connect(analyser);
    analyser.fftSize = 256;
    const bufferLength = analyser.frequencyBinCount;
    const dataArray = new Uint8Array(bufferLength);

    const checkSound = () => {
      analyser.getByteFrequencyData(dataArray);
      const volume = dataArray.reduce((sum, value) => sum + value, 0) / bufferLength;
      setSoundDetected(volume > 10);
      requestAnimationFrame(checkSound);
    };
    checkSound();
  }, [mediaStream]);

  useEffect(() => {
    if (permissionsGranted) {
      detectSound();
    }
  }, [permissionsGranted, detectSound]);

  return (
    <div className="continuous-auth-container">
      <h2> Continuous Authentication</h2>

      {showPermissionMessage && (
        <div className="permission-message">
          <p>Please allow access to your camera and microphone to start the exam.</p>
          <button onClick={getPermissions}>Allow Camera and Microphone</button>
        </div>
      )}

      {permissionsGranted ? (
        <>
          <Webcam
            audio={true}
            ref={webcamRef}
            screenshotFormat="image/jpeg"
            className="webcam"
            videoConstraints={{
              facingMode: "user",
              width: 720,
              height: 400,
            }}
          />
          <p>Camera and microphone are active for proctoring.</p>

          <div className="status-buttons-container">
            <div className="status-buttons">
              <button className={faceDetected ? "detected-btn" : "not-detected-btn"}>
                {faceDetected ? "Face Detected ✅" : "No Face Detected ❌"}
              </button>
              <button className={soundDetected ? "detected-btn" : "not-detected-btn"}>
                {soundDetected ? "Sound Detected 🔊" : "No Sound Detected 🔇"}
              </button>
            </div>

            <div className="captured-images">
              {currentImage && <img src={currentImage} alt="Captured" className="small-image" />}
            </div>
          </div>
        </>
      ) : (
        <p>Waiting for camera and microphone access...</p>
      )}

      {examTerminated && <p className="mismatch-count">Exam Terminated due to 3 mismatches.</p>}
    </div>
  );
};

export default ContinuousAuth;
