    baseline: null,
    lastAlert: 0,
    active: false,
    calibrationTime: 10000, // 10 Ø«ÙˆØ§Ù†ÙŠ Ù„Ù…Ø¹Ø§ÙŠØ±Ø© Ø§Ù„ØµÙˆØª
    calibrationStart: null,
    samples: [],
    isCalibrating: true
  });

