    "Ø£Ø°Ø§Ù†", "Ø§Ù„ØµÙ„Ø§Ø©", "Ù…Ø§Ù…Ø§", "Ø¨Ø§Ø¨Ø§", "Ù‡Ù„Ø§", "ØªØ¹Ø§Ù„ÙŠ", 
    "Ø§Ø³Ù…Ø¹", "Ù‡Ù†Ø§", "Ø´ÙˆÙ", "Ù‚ÙˆÙ„", "Ø³Ø§Ø¹Ø¯Ù†ÙŠ", "Ø¬Ø§ÙˆØ¨",
    "ØµØ­", "ØºÙ„Ø·", "ÙˆÙŠÙ†", "Ø¥Ø¬Ø§Ø¨Ø©", "Ø¬Ø§ÙˆØ¨Ù†ÙŠ", "Ø³Ø¤Ø§Ù„"
  ];

  useEffect(() => {
    const loadModels = async () => {
      await faceapi.nets.tinyFaceDetector.loadFromUri("/models");
      await faceapi.nets.faceLandmark68Net.loadFromUri("/models");
      console.log("Face Detection Model Loaded");
    };
    loadModels();
  }, []);

  const getPermissions = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ 
        video: { 
          width: 1280, 
          height: 720,
          facingMode: "user"
        }, 
        audio: {
          echoCancellation: true,
          noiseSuppression: true,
          autoGainControl: true
        }
      });
      setMediaStream(stream);
      setPermissionsGranted(true);
      setShowPermissionMessage(false);
      initializeSpeechRecognition();
      setupVoiceAnalysis(stream);
      setCalibrationMessage("Ø¬Ø§Ø±Ù Ù…Ø¹Ø§ÙŠØ±Ø© Ø§Ù„Ù†Ø¸Ø§Ù… Ø§Ù„ØµÙˆØªÙŠ... ÙŠØ±Ø¬Ù‰ Ø§Ù„ØªØ­Ø¯Ø« Ø¨ØµÙˆØª Ø·Ø¨ÙŠØ¹ÙŠ");
    } catch (error) {
      alert("ÙŠØ¬Ø¨ Ø§Ù„Ø³Ù…Ø§Ø­ Ø¨Ø§Ù„ÙˆØµÙˆÙ„ Ø¥Ù„Ù‰ Ø§Ù„ÙƒØ§Ù…ÙŠØ±Ø§ ÙˆØ§Ù„Ù…ÙŠÙƒØ±ÙˆÙÙˆÙ† Ù„Ø¨Ø¯Ø¡ Ø§Ù„Ø§Ø®ØªØ¨Ø§Ø±.");
      setShowPermissionMessage(true);
    }
  };

