        } else {
          // Ù…Ø±Ø­Ù„Ø© Ø§Ù„Ù…Ø±Ø§Ù‚Ø¨Ø© - Ø§ÙƒØªØ´Ø§Ù Ø§Ù„Ø£ØµÙˆØ§Øª Ø§Ù„Ù…ØªØ¹Ø¯Ø¯Ø©
          const isMultiple = detectMultipleVoices(currentProfile);
          
          if (isMultiple && Date.now() - voiceProfileRef.current.lastAlert > 5000) {
            setMultipleVoicesDetected(true);
            voiceProfileRef.current.lastAlert = Date.now();
            handleViolation("ØªÙ… Ø§ÙƒØªØ´Ø§Ù Ø£ØµÙˆØ§Øª Ù…ØªØ¹Ø¯Ø¯Ø©");
          } else {
