        }

        requestAnimationFrame(analyzeVoice);
      };

      analyzeVoice();
    } catch (error) {
      console.error("Voice analysis error:", error);
      setCalibrationMessage("Ø®Ø·Ø£ ÙÙŠ ØªØ­Ù„ÙŠÙ„ Ø§Ù„ØµÙˆØª. ÙŠØ±Ø¬Ù‰ ØªØ­Ø¯ÙŠØ« Ø§Ù„ØµÙØ­Ø© ÙˆØ§Ù„Ù…Ø­Ø§ÙˆÙ„Ø© Ù…Ø±Ø© Ø£Ø®Ø±Ù‰.");
    }
  };

  const createVoiceProfile = (dataArray) => {
    const profile = {
      lowRange: 0,    // 85-300Hz (Ø§Ù„Ø°ÙƒÙˆØ±)
      midRange: 0,    // 300-1000Hz (Ø§Ù„Ø¥Ù†Ø§Ø« ÙˆØ§Ù„Ø£Ø·ÙØ§Ù„)
      highRange: 0,   // 1000-4000Hz (Ø§Ù„ØªÙØ§ØµÙŠÙ„ Ø§Ù„ØµÙˆØªÙŠØ©)
      peakCount: 0,
      totalEnergy: 0,
      spectralFlatness: 0,
      formants: []
    };

    let sum = 0;
    let logSum = 0;
    let geometricMean = 0;
    let arithmeticMean = 0;

    for (let i = 0; i < dataArray.length; i++) {
      const freq = i * audioContextRef.current.sampleRate / analyserRef.current.fftSize;
      const value = Math.pow(10, dataArray[i]/20); // ØªØ­ÙˆÙŠÙ„ dB Ø¥Ù„Ù‰ linear

      if (freq >= 85 && freq < 300) {
        profile.lowRange += value;
      } else if (freq >= 300 && freq < 1000) {
        profile.midRange += value;
      } else if (freq >= 1000 && freq < 4000) {
        profile.highRange += value;
      }

      if (dataArray[i] > -40) profile.peakCount++;
      profile.totalEnergy += value;
      
      sum += value;
      logSum += Math.log(value + 0.000001); // ØªØ¬Ù†Ø¨ Ø§Ù„Ù‚ÙŠÙ… Ø§Ù„Ø³Ø§Ù„Ø¨Ø©
    }

    // Ø­Ø³Ø§Ø¨ Ø§Ù„ØªØ³Ø·Ø­ Ø§Ù„Ø·ÙŠÙÙŠ (Spectral Flatness)
    arithmeticMean = sum / dataArray.length;
    geometricMean = Math.exp(logSum / dataArray.length);
    profile.spectralFlatness = geometricMean / (arithmeticMean + 0.000001);

    // Ø­Ø³Ø§Ø¨ Ø§Ù„ÙÙˆØ±Ù…Ø§Ù†ØªØ§Øª (ØªØ­Ø¯ÙŠØ¯ Ø§Ù„ØªØ±Ø¯Ø¯Ø§Øª Ø§Ù„Ù…Ù‡ÙŠÙ…Ù†Ø©)
    const peaks = findPeaks(dataArray);
    profile.formants = peaks.slice(0, 3); // Ø£ÙˆÙ„ 3 ÙÙˆØ±Ù…Ø§Ù†ØªØ§Øª

    return profile;
  };

  const findPeaks = (dataArray) => {
    const peaks = [];
    const minDistance = 50; // Ø§Ù„Ø­Ø¯ Ø§Ù„Ø£Ø¯Ù†Ù‰ Ù„Ù„Ù…Ø³Ø§ÙØ© Ø¨ÙŠÙ† Ø§Ù„Ù‚Ù…Ù… (Ø¨Ø§Ù„Ù‡Ø±ØªØ²)
    
    for (let i = 2; i < dataArray.length - 2; i++) {
