    // ØªØ±ØªÙŠØ¨ Ø§Ù„Ù‚Ù…Ù… Ø­Ø³Ø¨ Ø§Ù„Ø³Ø¹Ø©
    peaks.sort((a, b) => b.amplitude - a.amplitude);
    
    // ØªØµÙÙŠØ© Ø§Ù„Ù‚Ù…Ù… Ø§Ù„Ù‚Ø±ÙŠØ¨Ø© Ù…Ù† Ø¨Ø¹Ø¶Ù‡Ø§
    const filteredPeaks = [];
    const freqUsed = new Set();
    
    for (const peak of peaks) {
      let tooClose = false;
      for (const usedFreq of freqUsed) {
        if (Math.abs(peak.freq - usedFreq) < minDistance) {
          tooClose = true;
          break;
        }
      }
      if (!tooClose) {
        filteredPeaks.push(peak.freq);
        freqUsed.add(peak.freq);
      }
    }
    
    return filteredPeaks;
