  const detectMultipleVoices = (currentProfile) => {
    const baseline = voiceProfileRef.current.baseline;
    if (!baseline) return false;
    
    // 1. Ø­Ø³Ø§Ø¨ Ù†Ø³Ø¨Ø© Ø§Ù„ØªØºÙŠØ± ÙÙŠ Ù†Ø·Ø§Ù‚Ø§Øª Ø§Ù„ØªØ±Ø¯Ø¯
    const lowDiff = Math.abs(currentProfile.lowRange - baseline.lowRange) / baseline.lowRange;
    const midDiff = Math.abs(currentProfile.midRange - baseline.midRange) / baseline.midRange;
    const highDiff = Math.abs(currentProfile.highRange - baseline.highRange) / baseline.highRange;
    
    // 2. ØªØºÙŠØ± ÙÙŠ Ø¹Ø¯Ø¯ Ø§Ù„Ù‚Ù…Ù…
    const peakDiff = Math.abs(currentProfile.peakCount - baseline.peakCount);
    
    // 3. ØªØºÙŠØ± ÙÙŠ Ø§Ù„ØªØ³Ø·Ø­ Ø§Ù„Ø·ÙŠÙÙŠ (ÙŠØ´ÙŠØ± Ø¥Ù„Ù‰ ØªØºÙŠØ± ÙÙŠ Ø·Ø¨ÙŠØ¹Ø© Ø§Ù„ØµÙˆØª)
    const flatnessDiff = Math.abs(currentProfile.spectralFlatness - baseline.spectralFlatness);
    
    // 4. ØªØ­Ù„ÙŠÙ„ Ø§Ù„ÙÙˆØ±Ù…Ø§Ù†ØªØ§Øª (Ø§Ù„ØªØºÙŠØ± ÙÙŠ Ø§Ù„ØªØ±Ø¯Ø¯Ø§Øª Ø§Ù„Ù…Ù‡ÙŠÙ…Ù†Ø©)
    let formantDiff = 0;
    for (let i = 0; i < 3; i++) {
