    formantDiff /= 3;
    
    // Ø´Ø±ÙˆØ· Ø§ÙƒØªØ´Ø§Ù Ø§Ù„Ø£ØµÙˆØ§Øª Ø§Ù„Ù…ØªØ¹Ø¯Ø¯Ø©
    const isMultiple = (
