      (peakDiff > 10) && // ØªØºÙŠØ± ÙƒØ¨ÙŠØ± ÙÙŠ Ø¹Ø¯Ø¯ Ø§Ù„Ù‚Ù…Ù…
      (flatnessDiff > 0.3) && // ØªØºÙŠØ± ÙÙŠ Ø·Ø¨ÙŠØ¹Ø© Ø§Ù„ØµÙˆØª
      (formantDiff > 0.2) // ØªØºÙŠØ± ÙÙŠ Ø§Ù„ÙÙˆØ±Ù…Ø§Ù†ØªØ§Øª Ø§Ù„Ø£Ø³Ø§Ø³ÙŠØ©
    );
    
    console.log("Voice analysis:", {
      lowDiff, midDiff, highDiff, 
      peakDiff, flatnessDiff, formantDiff,
      isMultiple
    });
    
    return isMultiple;
  };

  const checkForbiddenWords = (text) => {
