import React, { useState, useEffect, useRef, useCallback } from "react";
import * as faceapi from "face-api.js";
import Webcam from "react-webcam";
import "./ContinuousAuth.css";

const ContinuousAuth = () => {
  const webcamRef = useRef(null);
  const [faceDetected, setFaceDetected] = useState(false);
  const [soundDetected, setSoundDetected] = useState(false);
  const [currentImage, setCurrentImage] = useState(null);
  const [permissionsGranted, setPermissionsGranted] = useState(false);
  const [examTerminated, setExamTerminated] = useState(false);
  const [mediaStream, setMediaStream] = useState(null);
  const [showPermissionMessage, setShowPermissionMessage] = useState(true);
  const [noFaceDuration, setNoFaceDuration] = useState(0);
  const [transcript, setTranscript] = useState("");
  const [violations, setViolations] = useState(0);
  const recognitionRef = useRef(null);
  const [calibrationProgress, setCalibrationProgress] = useState(0);
  const [calibrationMessage, setCalibrationMessage] = useState("");

  // نظام كشف الأصوات المتعددة المحسن
  const [multipleVoicesDetected, setMultipleVoicesDetected] = useState(false);
  const audioContextRef = useRef(null);
  const analyserRef = useRef(null);
  const voiceProfileRef = useRef({
    baseline: null,
    lastAlert: 0,
    active: false,
    calibrationTime: 10000, // 10 ثواني لمعايرة الصوت
    calibrationStart: null,
    samples: [],
    isCalibrating: true
  });

  // قائمة الكلمات المحظورة
  const forbiddenWords = [
    "أذان", "الصلاة", "ماما", "بابا", "هلا", "تعالي", 
    "اسمع", "هنا", "شوف", "قول", "ساعدني", "جاوب",
    "صح", "غلط", "وين", "إجابة", "جاوبني", "سؤال"
  ];

  useEffect(() => {
    const loadModels = async () => {
      await faceapi.nets.tinyFaceDetector.loadFromUri("/models");
      await faceapi.nets.faceLandmark68Net.loadFromUri("/models");
      console.log("Face Detection Model Loaded");
    };
    loadModels();
  }, []);

  const getPermissions = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ 
        video: { 
          width: 1280, 
          height: 720,
          facingMode: "user"
        }, 
        audio: {
          echoCancellation: true,
          noiseSuppression: true,
          autoGainControl: true
        }
      });
      setMediaStream(stream);
      setPermissionsGranted(true);
      setShowPermissionMessage(false);
      initializeSpeechRecognition();
      setupVoiceAnalysis(stream);
      setCalibrationMessage("جارِ معايرة النظام الصوتي... يرجى التحدث بصوت طبيعي");
    } catch (error) {
      alert("يجب السماح بالوصول إلى الكاميرا والميكروفون لبدء الاختبار.");
      setShowPermissionMessage(true);
    }
  };

  // نظام تحليل الصوت المحسن
  const setupVoiceAnalysis = (stream) => {
    try {
      audioContextRef.current = new (window.AudioContext || window.webkitAudioContext)();
      analyserRef.current = audioContextRef.current.createAnalyser();
      analyserRef.current.fftSize = 2048;
      
      const source = audioContextRef.current.createMediaStreamSource(stream);
      source.connect(analyserRef.current);

      voiceProfileRef.current.calibrationStart = Date.now();
      voiceProfileRef.current.isCalibrating = true;
      voiceProfileRef.current.samples = [];

      const analyzeVoice = () => {
        if (!analyserRef.current) return;

        const bufferLength = analyserRef.current.frequencyBinCount;
        const dataArray = new Float32Array(bufferLength);
        analyserRef.current.getFloatFrequencyData(dataArray);

        // إنشاء ملف تعريف صوتي للعينة الحالية
        const currentProfile = createVoiceProfile(dataArray);

        if (voiceProfileRef.current.isCalibrating) {
          // مرحلة المعايرة - جمع العينات
          voiceProfileRef.current.samples.push(currentProfile);
          
          // تحديث شريط التقدم
          const elapsed = Date.now() - voiceProfileRef.current.calibrationStart;
          const progress = Math.min(100, (elapsed / voiceProfileRef.current.calibrationTime) * 100);
          setCalibrationProgress(progress);
          
          // التحقق من انتهاء وقت المعايرة
          if (elapsed > voiceProfileRef.current.calibrationTime) {
            finalizeCalibration();
          }
        } else {
          // مرحلة المراقبة - اكتشاف الأصوات المتعددة
          const isMultiple = detectMultipleVoices(currentProfile);
          
          if (isMultiple && Date.now() - voiceProfileRef.current.lastAlert > 5000) {
            setMultipleVoicesDetected(true);
            voiceProfileRef.current.lastAlert = Date.now();
            handleViolation("تم اكتشاف أصوات متعددة");
          } else {
            setMultipleVoicesDetected(false);
          }
        }

        requestAnimationFrame(analyzeVoice);
      };

      analyzeVoice();
    } catch (error) {
      console.error("Voice analysis error:", error);
      setCalibrationMessage("خطأ في تحليل الصوت. يرجى تحديث الصفحة والمحاولة مرة أخرى.");
    }
  };

  const createVoiceProfile = (dataArray) => {
    const profile = {
      lowRange: 0,    // 85-300Hz (الذكور)
      midRange: 0,    // 300-1000Hz (الإناث والأطفال)
      highRange: 0,   // 1000-4000Hz (التفاصيل الصوتية)
      peakCount: 0,
      totalEnergy: 0,
      spectralFlatness: 0,
      formants: []
    };

    let sum = 0;
    let logSum = 0;
    let geometricMean = 0;
    let arithmeticMean = 0;

    for (let i = 0; i < dataArray.length; i++) {
      const freq = i * audioContextRef.current.sampleRate / analyserRef.current.fftSize;
      const value = Math.pow(10, dataArray[i]/20); // تحويل dB إلى linear

      if (freq >= 85 && freq < 300) {
        profile.lowRange += value;
      } else if (freq >= 300 && freq < 1000) {
        profile.midRange += value;
      } else if (freq >= 1000 && freq < 4000) {
        profile.highRange += value;
      }

      if (dataArray[i] > -40) profile.peakCount++;
      profile.totalEnergy += value;
      
      sum += value;
      logSum += Math.log(value + 0.000001); // تجنب القيم السالبة
    }

    // حساب التسطح الطيفي (Spectral Flatness)
    arithmeticMean = sum / dataArray.length;
    geometricMean = Math.exp(logSum / dataArray.length);
    profile.spectralFlatness = geometricMean / (arithmeticMean + 0.000001);

    // حساب الفورمانتات (تحديد الترددات المهيمنة)
    const peaks = findPeaks(dataArray);
    profile.formants = peaks.slice(0, 3); // أول 3 فورمانتات

    return profile;
  };

  const findPeaks = (dataArray) => {
    const peaks = [];
    const minDistance = 50; // الحد الأدنى للمسافة بين القمم (بالهرتز)
    
    for (let i = 2; i < dataArray.length - 2; i++) {
      if (dataArray[i] > dataArray[i-1] && 
          dataArray[i] > dataArray[i-2] && 
          dataArray[i] > dataArray[i+1] && 
          dataArray[i] > dataArray[i+2]) {
        peaks.push({
          freq: i * audioContextRef.current.sampleRate / analyserRef.current.fftSize,
          amplitude: dataArray[i]
        });
      }
    }
    
    // ترتيب القمم حسب السعة
    peaks.sort((a, b) => b.amplitude - a.amplitude);
    
    // تصفية القمم القريبة من بعضها
    const filteredPeaks = [];
    const freqUsed = new Set();
    
    for (const peak of peaks) {
      let tooClose = false;
      for (const usedFreq of freqUsed) {
        if (Math.abs(peak.freq - usedFreq) < minDistance) {
          tooClose = true;
          break;
        }
      }
      if (!tooClose) {
        filteredPeaks.push(peak.freq);
        freqUsed.add(peak.freq);
      }
    }
    
    return filteredPeaks;
  };

  const finalizeCalibration = () => {
    const samples = voiceProfileRef.current.samples;
    
    // حساب المتوسطات
    const baseline = {
      lowRange: 0,
      midRange: 0,
      highRange: 0,
      peakCount: 0,
      totalEnergy: 0,
      spectralFlatness: 0,
      formants: []
    };
    
    // حساب المتوسط لكل خاصية
    for (const sample of samples) {
      baseline.lowRange += sample.lowRange;
      baseline.midRange += sample.midRange;
      baseline.highRange += sample.highRange;
      baseline.peakCount += sample.peakCount;
      baseline.totalEnergy += sample.totalEnergy;
      baseline.spectralFlatness += sample.spectralFlatness;
    }
    
    baseline.lowRange /= samples.length;
    baseline.midRange /= samples.length;
    baseline.highRange /= samples.length;
    baseline.peakCount /= samples.length;
    baseline.totalEnergy /= samples.length;
    baseline.spectralFlatness /= samples.length;
    
    // حساب الفورمانتات الأكثر شيوعاً
    const formantCounts = {};
    samples.forEach(sample => {
      sample.formants.forEach((freq, idx) => {
        const roundedFreq = Math.round(freq / 10) * 10;
        const key = `${idx}-${roundedFreq}`;
        formantCounts[key] = (formantCounts[key] || 0) + 1;
      });
    });
    
    // اختيار الفورمانتات الأكثر تكراراً
    for (let i = 0; i < 3; i++) {
      const relevant = Object.entries(formantCounts)
        .filter(([key]) => key.startsWith(`${i}-`))
        .sort((a, b) => b[1] - a[1]);
      
      if (relevant.length > 0) {
        const mostCommon = relevant[0][0].split('-')[1];
        baseline.formants.push(parseInt(mostCommon));
      } else {
        baseline.formants.push(0);
      }
    }
    
    voiceProfileRef.current.baseline = baseline;
    voiceProfileRef.current.isCalibrating = false;
    setCalibrationMessage("اكتملت المعايرة بنجاح. يمكنك بدء الاختبار الآن.");
    setTimeout(() => setCalibrationMessage(""), 3000);
    console.log("Voice calibration complete", baseline);
  };

  const detectMultipleVoices = (currentProfile) => {
    const baseline = voiceProfileRef.current.baseline;
    if (!baseline) return false;
    
    // 1. حساب نسبة التغير في نطاقات التردد
    const lowDiff = Math.abs(currentProfile.lowRange - baseline.lowRange) / baseline.lowRange;
    const midDiff = Math.abs(currentProfile.midRange - baseline.midRange) / baseline.midRange;
    const highDiff = Math.abs(currentProfile.highRange - baseline.highRange) / baseline.highRange;
    
    // 2. تغير في عدد القمم
    const peakDiff = Math.abs(currentProfile.peakCount - baseline.peakCount);
    
    // 3. تغير في التسطح الطيفي (يشير إلى تغير في طبيعة الصوت)
    const flatnessDiff = Math.abs(currentProfile.spectralFlatness - baseline.spectralFlatness);
    
    // 4. تحليل الفورمانتات (التغير في الترددات المهيمنة)
    let formantDiff = 0;
    for (let i = 0; i < 3; i++) {
      if (baseline.formants[i] > 0) {
        formantDiff += Math.abs(currentProfile.formants[i] - baseline.formants[i]) / baseline.formants[i];
      }
    }
    formantDiff /= 3;
    
    // شروط اكتشاف الأصوات المتعددة
    const isMultiple = (
      (lowDiff > 0.5 || midDiff > 0.5 || highDiff > 0.5) && // تغير كبير في نطاق ترددي واحد على الأقل
      (peakDiff > 10) && // تغير كبير في عدد القمم
      (flatnessDiff > 0.3) && // تغير في طبيعة الصوت
      (formantDiff > 0.2) // تغير في الفورمانتات الأساسية
    );
    
    console.log("Voice analysis:", {
      lowDiff, midDiff, highDiff, 
      peakDiff, flatnessDiff, formantDiff,
      isMultiple
    });
    
    return isMultiple;
  };

  const checkForbiddenWords = (text) => {
    const foundWords = forbiddenWords.filter(word => 
      text.toLowerCase().includes(word.toLowerCase())
    );
    
    if (foundWords.length > 0) {
      handleViolation(`تم اكتشاف كلمات محظورة: ${foundWords.join(', ')}`);
    }
  };

  const handleViolation = (reason) => {
    const newViolations = violations + 1;
    setViolations(newViolations);
    
    alert(`⚠️ انذار: ${reason} (المخالفة ${newViolations}/3)`);
    
    if (newViolations >= 3) {
      setExamTerminated(true);
      if (mediaStream) {
        mediaStream.getTracks().forEach(track => track.stop());
      }
    }
  };

  const initializeSpeechRecognition = () => {
    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
    if (!SpeechRecognition) {
      console.error("Speech Recognition API not supported in this browser");
      return;
    }

    recognitionRef.current = new SpeechRecognition();
    recognitionRef.current.continuous = true;
    recognitionRef.current.interimResults = false;
    recognitionRef.current.lang = "ar-SA";

    recognitionRef.current.onresult = (event) => {
      const last = event.results.length - 1;
      const text = event.results[last][0].transcript;
      setTranscript(prev => prev + " " + text);
      checkForbiddenWords(text);
    };

    recognitionRef.current.onerror = (event) => {
      console.error("Speech recognition error", event.error);
    };

    recognitionRef.current.start();
    recognitionRef.current.onend = () => {
      recognitionRef.current.start();
    };
  };

  useEffect(() => {
    if (mediaStream) {
      const tracks = mediaStream.getTracks();
      tracks.forEach((track) => {
        track.enabled = true;
      });
    }

    return () => {
      if (recognitionRef.current) {
        recognitionRef.current.stop();
      }
      if (audioContextRef.current) {
        audioContextRef.current.close();
      }
      if (mediaStream) {
        mediaStream.getTracks().forEach(track => track.stop());
      }
    };
  }, [mediaStream]);

  const detectFace = async () => {
    if (webcamRef.current && webcamRef.current.video.readyState === 4) {
      const video = webcamRef.current.video;
      const detections = await faceapi.detectAllFaces(video, new faceapi.TinyFaceDetectorOptions());
      const isFacePresent = detections.length === 1; // نريد وجه واحد فقط
      
      setFaceDetected(isFacePresent);
      
      if (isFacePresent) {
        setNoFaceDuration(0);
      } else {
        setNoFaceDuration(prev => prev + 1);
      }
    }
  };

  useEffect(() => {
    if (permissionsGranted) {
      const interval = setInterval(detectFace, 1000);
      return () => clearInterval(interval);
    }
  }, [permissionsGranted]);

  useEffect(() => {
    if (noFaceDuration >= 10) {
      handleViolation("لم يتم اكتشاف وجهك لمدة 10 ثواني");
      setNoFaceDuration(0);
    }
  }, [noFaceDuration]);

  const detectSound = useCallback(() => {
    if (!mediaStream) return;

    const audioContext = new AudioContext();
    const analyser = audioContext.createAnalyser();
    const source = audioContext.createMediaStreamSource(mediaStream);
    source.connect(analyser);
    analyser.fftSize = 256;
    const bufferLength = analyser.frequencyBinCount;
    const dataArray = new Uint8Array(bufferLength);

    const checkSound = () => {
      analyser.getByteFrequencyData(dataArray);
      const volume = dataArray.reduce((sum, value) => sum + value, 0) / bufferLength;
      setSoundDetected(volume > 10);
      requestAnimationFrame(checkSound);
    };
    checkSound();
  }, [mediaStream]);

  useEffect(() => {
    if (permissionsGranted) {
      detectSound();
    }
  }, [permissionsGranted, detectSound]);

  return (
    <div className="continuous-auth-container">
      <h2>نظام مراقبة الاختبارات الإلكترونية</h2>

      {showPermissionMessage && (
        <div className="permission-message">
          <h3>إعدادات مراقبة الاختبار</h3>
          <p>هذا الاختبار يتطلب الوصول إلى الكاميرا والميكروفون لأغراض المراقبة.</p>
          <div className="permission-requirements">
            <p>✓ يجب تفعيل كشف الوجه</p>
            <p>✓ يجب تفعيل الميكروفون</p>
            <p>✓ يجب أن تكون في بيئة هادئة</p>
          </div>
          <button className="permission-button" onClick={getPermissions}>
            تفعيل الكاميرا والميكروفون
          </button>
        </div>
      )}

      {calibrationMessage && (
        <div className="calibration-message">
          <p>{calibrationMessage}</p>
          {voiceProfileRef.current.isCalibrating && (
            <div className="calibration-progress">
              <div 
                className="progress-bar" 
                style={{ width: `${calibrationProgress}%` }}
              ></div>
            </div>
          )}
        </div>
      )}

      {permissionsGranted && !examTerminated && (
        <div className="monitoring-container">
          <div className="video-section">
            <Webcam
              audio={false}
              ref={webcamRef}
              screenshotFormat="image/jpeg"
              className="webcam"
              videoConstraints={{
                facingMode: "user",
                width: 1280,
                height: 720,
              }}
            />
            <div className="status-indicators">
              <div className={`status-indicator ${faceDetected ? "active" : "inactive"}`}>
                {faceDetected ? "تم اكتشاف الوجه" : "لم يتم اكتشاف الوجه"}
                {!faceDetected && noFaceDuration > 0 && (
                  <span className="warning-text"> ({noFaceDuration} ثانية)</span>
                )}
              </div>
              <div className={`status-indicator ${soundDetected ? "active" : "inactive"}`}>
                {soundDetected ? "تم اكتشاف الصوت" : "لم يتم اكتشاف الصوت"}
              </div>
              {multipleVoicesDetected && (
                <div className="status-indicator warning">
                  تم اكتشاف أصوات متعددة!
                </div>
              )}
            </div>
          </div>

          <div className="transcript-section">
            <div className="transcript-box">
              <div className="transcript-header">
                <span>نص الكلام</span>
                <span className="violation-counter">المخالفات: {violations}/3</span>
              </div>
              <div className="transcript-content" dir="rtl">
                {transcript || "بانتظار المدخل الصوتي..."}
              </div>
              <div className={`voice-detection-status ${multipleVoicesDetected ? "multiple" : ""}`}>
                {multipleVoicesDetected 
                  ? "تم اكتشاف أصوات متعددة!" 
                  : "نظام تحليل الصوت نشط"}
              </div>
            </div>
          </div>
        </div>
      )}

      {examTerminated && (
        <div className="termination-message">
          <h3>✖ تم إنهاء الاختبار</h3>
          <p>نظرًا لوجود مخالفات متعددة. يرجى التواصل مع المشرف.</p>
          <p>إجمالي المخالفات: {violations}</p>
        </div>
      )}
    </div>
  );
};

export default ContinuousAuth;
